float shoe(vec3 p)
{
    float acc = max(length(p-vec3(0.,0.,-.3))-.15, p.y);
    float sock = max(max(length(p.xz)-.1-.01*sin(p.y*40.-3.), p.y), -(p.y+.2));
    acc = min(acc, sock);
    acc = min(acc, max(max(length(p.xz*vec2(1.,.5)-vec2(0.,-.1))-.15, p.y), -(p.y+.025)));
    return acc;
}
vec2 leg(vec3 p)
{
    vec2 acc = vec2(1000., -1.);
    float leg = max(length(p.xz)-.03, -p.y);
    leg = max(leg, (p.y-2.));
    acc = _min(acc, vec2(leg, 1.));
    acc = _min(acc, vec2(shoe(p-vec3(0.,2., 0.)), 0.));
    return acc;
}

float hand(vec3 p)
{
    float acc = 1000.;
    vec2 sz = vec2(.025,.1);
    vec3 pt = p-vec3(0.,.0,0.);
    //pt.xy *= r2d(1.5);
pt.yz *= r2d(.0);
    float thumb = line(pt, sz*vec2(1.,.5), -3.);
    acc = min(acc, thumb);
    vec3 p1 = p+vec3(.1,0.05,0.);
    p1.xy *= r2d(.5);
    float first = line(p1, sz, 3.);
    acc = min(acc, first);
    vec3 p2 = p+vec3(.2,0.0,0.);
    p2.xy *= r2d(.6);
    float snd = line(p2, sz, 1.);
    acc = min(acc, snd);
    vec3 p3 = p+vec3(.25,-0.05,0.);
    p3.xy *= r2d(.9);
    float third = line(p3, sz, 2.);
    acc = min(acc, third);

    acc = smin(acc, length(p*vec3(1.2,1.1,2.)-vec3(-0.07,0.15,0.))-.14, 1.);
    return acc;
}

vec2 map(vec3 p)
{
    vec2 acc = vec2(1000., -1.);

    vec3 pbody = p;
    pbody.yz *= r2d(.1);
    float body = max(length(pbody.xy)-1.-.1*cos(abs(pbody.z)*5.), abs(pbody.z)-.2);
    acc = _min(acc, vec2(body, 0.));

    float legSep = .3;
    vec3 rlegp = p-vec3(legSep,0.,0.);
    rlegp.x += cos(rlegp.y*3.)*.05;
    acc = _min(acc, leg(rlegp));
    vec3 llegp = p-vec3(-legSep,0.,0.);
        llegp.x -= cos(llegp.y*3.)*.05;
    llegp.yz *= r2d(-pow(sat(llegp.y-1.), 5.)*.05);
    llegp.xz *= r2d(.25);
    acc = _min(acc, leg(llegp));

    vec3 plhand = p+vec3(1.55,-0.6,0.15);
    plhand.xy *= r2d(.35);
    plhand.yz *= r2d(-.7);

    acc = _min(acc, vec2(hand(plhand), 2.));

    vec3 prhand = p+vec3(-1.58,-0.6,0.2);
    prhand.x = -prhand.x;
    prhand.xy *= r2d(.5);
    prhand.yz *= r2d(-.7);

    acc = _min(acc, vec2(hand(prhand), 2.));

    vec3 plarma = p+vec3(1.1,-0.5,0.);
    plarma.xy *= r2d(-.25);
    float larma = line(plarma, vec2(.1,.3), -0.5);
    acc = _min(acc, vec2(larma, 0.));

    vec3 plarmb = p+vec3(1.3,-.8,0.);
    plarmb.xy *= r2d(-1.75);
    float larmb = line(plarmb, vec2(.07,.15), 0.);
    acc = _min(acc, vec2(larmb, 0.));

    vec3 prarma = p+vec3(-1.1,-0.5,0.);
    prarma.xy *= r2d(.25);
    float rarma = line(prarma, vec2(.1,.3), -.75);
    acc = _min(acc, vec2(rarma, 0.));

    vec3 prarmb = p+vec3(-1.3,-.8,0.);
    prarmb.xy *= r2d(1.75);
    prarmb.yz *= r2d(.35);
    float rarmb = line(prarmb, vec2(.07,.15), 0.);
    acc = _min(acc, vec2(rarmb, 0.));

    return acc;
}
vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.01,0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}
float accMarch;
vec4 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.01)
            return vec4(res.x, distance(p, ro), res.y, float(i));
        p+= res.x*rd;
        accMarch += (1.-sat(res.x/0.7))*(p.y-1.2 > 0. ? .7 : 1.);
    }
    return vec4(-1.);
}

float eyeShape(vec2 uv)
{
    float a = atan(uv.y, uv.x*1.5+.1);
    a = clamp(a, -2., -.8);
    float rep = 0.3;
    float b = mod(a+rep*.5,rep)-rep*.5;
    vec2 uv2 = vec2(sin(b), cos(b))*length(uv);
    uv *= r2d(.1);
    float fur = line(uv2-vec2(0.0,.35), vec2(.01,.04));
    return min(length(uv*vec2(1.*(1.-sat(pow(sat(uv.y),1.5)*.75+.2)),.5))-.175, fur);
}

vec3 drawEyes(vec3 rgb, vec2 uv)
{
    vec2 euv = uv;

    float shp = 400.;
    uv.x = abs(uv.x)-.35;
    float eyesShape = eyeShape(uv);
    vec3 col = mix(rgb, vec3(1.), 1.-sat(eyesShape*shp));
    col = mix(col, vec3(0.), 1.-sat((abs(eyesShape)-.01)*shp));

    float offX = 0.3;
    vec2 posEyes = vec2(sin(iTime*.5)*.1,0.);
    vec2 uvl = euv-vec2(offX, 0.)-posEyes;
    vec2 uvr = euv+vec2(offX, 0.)-posEyes;
    float eyeSz = .1;
    vec2 eyeScale = vec2(1.,.6); 
    vec2 offTick = vec2(0.,-.05);
    vec2 uvl2 = uvl-offTick;
    vec2 uvr2 = uvr-offTick;
    float al = atan(uvl2.y,-uvl2.x);
    float ar = atan(uvr2.y,-uvr2.x);

    float eyes = min(max(length(uvl*eyeScale)-eyeSz, -sin(clamp(-al,0.,1.)*7.)), max(length(uvr*eyeScale)-eyeSz, -sin(clamp(-ar,0.,1.)*7.)));
    eyes = max(eyes, eyesShape);
    col = mix(col, vec3(0.), 1.-sat(eyes*shp));
    return col;
}

vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.604,0.718,0.631);

    vec3 ro = vec3(2.*sin(iTime*.5),sin(iTime*.25),-7.);
    vec3 ta = vec3(0.,0.5,0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(uv, rd);
    float shp = 400.;
    accMarch = 0.;
    vec4 res = trace(ro, rd, 128);
    if (res.y > 0.)
    {
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        vec3 rgb = vec3(0.851,0.635,0.329);// n *.5+.5;
        if (res.z == 2.)
            rgb = vec3(1.);
        if (res.z == 1.)
            rgb = vec3(0.);
        if (res.z == 0.)
        {
            rgb = mix(rgb, vec3(0.), 1.-sat((abs(length(p.xy)-1.05)-.02)*shp));
            vec2 tickuv = p.xy;
            vec2 tickuv2 = p.xy;
            float atick = atan(tickuv.y,tickuv.x);
            float tickrep = TAU/12.;
            float tickrep2 = TAU/4.;
            float angletick2 = mod(atick + tickrep2*.5, tickrep2)-tickrep2*.5;
            float angletick = mod(atick + tickrep*.5, tickrep)-tickrep*.5;
            tickuv = vec2(sin(angletick), cos(angletick))*length(tickuv);
            tickuv2 =  vec2(sin(angletick2), cos(angletick2))*length(tickuv2);
            float ticks = line(tickuv-vec2(0.,.85), vec2(.01,.08));
            rgb = mix(rgb, vec3(0.), 1.-sat(ticks*shp));
            float ticks2 = line(tickuv2-vec2(0.,.8), vec2(.04,.12));
            rgb = mix(rgb, vec3(0.), 1.-sat(ticks2*shp));
            rgb = drawEyes(rgb, p.xy-vec2(0.,-.1));
            rgb = mix(rgb, vec3(0.), 1.-sat((length(p.xy)-.07)*shp));
            float mouth = line((p.xy-vec2(0.,0.4-.05*cos(p.x*5.)))*r2d(PI*.5), vec2(.02,.15));
            rgb = mix(rgb, vec3(0.), 1.-sat(mouth*shp));
        }

        col = rgb;
        if (p.y > 1.2)
        {
            col = mix(col, vec3(0.), 1.-pow(sat(-dot(n, rd)+.5),20.));
        }
    }
    col = mix(col, vec3(0.), pow(sat(accMarch*.09),100.));
    //col += pow(accMarch, 1.)*vec3(1.)*.1;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;

    vec3 col = rdr(uv);
    col *= (1.-sat(length(uv)-.3))+.1;
    col = pow(col*1., vec3(2.5));
    fragColor = vec4(col,1.0);
}

