void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float i;
    if (iMouse.z>0.0) { i=iMouse.x/iResolution.x; }
    else { i=mod((iTime*.5),32.0)/32.0; }
    vec3 col;
    if (uv.x<.497) { col=texture(iChannel0,vec3(uv*vec2(2.02,1.0),i)).rrr; }
    else if (uv.x>.503) { col=texture(iChannel1,vec3(uv*vec2(2.02,1.0)-vec2(1.01606,0.),i)).rgb; }
    else { col=vec3(0.,0.,0.); }
    // Output to screen
    fragColor = vec4(col,1.0);
}

// "Aperiodic Penrose Tiling 2" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define AA   0  // (= 0/1) optional antialising

float SmoothMax (float a, float b, float r);
mat3 VToRMat (vec3 v, float a);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
vec3 HsvToRgb (vec3 c);
float Fbm2 (vec2 p);
vec4 LoadTx (vec2 uv);

vec4 qv4;
vec3 ltDir;
float tCur, dstFar, rad[3], pFac[3];
int idObj, mode;
int nFrame;
const float pi = 3.1415927;

#define VAR_ZERO min (nFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 offSet;
  float dMin, d, r, h;
  dMin = dstFar;
  q = p;
  q.y -= 1.08;
  offSet = Rot2D (vec2 (0.2, 0.), -0.05 * pi * tCur);
  qv4 = LoadTx ((pFac[0] / rad[0]) * q.xy + 0.5 + offSet);
  r = length (q.xy);
  d = r - rad[0];
  h = -0.02 * smoothstep (0.05, 0.3, abs (qv4.x)) * (1. - smoothstep (-0.05, -0.01, r - rad[0]));
  d = SmoothMax (abs (q.z) - (h + 0.01), d, 0.01);
  DMIN (1);
  q = p;
  q.y -= 0.05;
  qv4 = LoadTx ((pFac[1] / rad[1]) * q.xz + 0.5);
  r = length (q.xz);
  d = r - rad[1];
  h = -0.02 * smoothstep (0.03, 0.25, abs (qv4.x)) * (1. - smoothstep (-0.05, -0.01, r - rad[1]));
  d = SmoothMax (abs (q.y - 0.5 * h) - 0.5 * h - 0.025, d, 0.01);
  DMIN (2);
  return 0.7 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    if (d < 0.0005 || dHit > dstFar || p.y < 0.) break;
    dHit += d;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.01 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.2 + 0.8 * sh;
}

vec3 BgCol (vec3 rd)
{
  rd.y = abs (rd.y);
  return mix (vec3 (0.3, 0.35, 0.7), vec3 (0.8, 0.8, 0.8),
     clamp (2. * (Fbm2 (4. * rd.xz / rd.y + 0.1 * tCur) - 0.1) * rd.y, 0., 1.));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colR, gCol, sCol, vn, ror, rdr;
  float dstObj, dstObjR, dstFlr, nDotL, sh, dm, att;
  bool isRefl;
  rad[0] = 1.;
  rad[1] = 1.3;
  rad[2] = 6.;
  pFac[0] = 0.3;
  pFac[1] = 0.35;
  pFac[2] = 0.5;
  gCol = vec3 (0.8, 0.6, 0.1);
  sCol = vec3 (0.8, 0.8, 0.85);
  dstFlr = (rd.y < 0.) ? - ro.y / rd.y : dstFar;
  dstObj = ObjRay (ro, rd);
  dstObjR = dstFar;
  isRefl = false;
  if (dstFlr < min (dstObj, dstFar)) {
    ror = ro + dstFlr * rd;
    rdr = reflect (rd, vec3 (0., 1., 0.));
    ror += 0.01 * rdr;
    dstObjR = ObjRay (ror, rdr);
    isRefl = true;
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    nDotL = max (dot (vn, ltDir), 0.);
    nDotL *= nDotL;
    if (idObj == 1) col = gCol;
    else if (idObj == 2) {
      col = sCol;
      if (length (ro.xz) < rad[1] - 0.005 && ro.y < 0.065) {
        qv4 = LoadTx ((pFac[1] / rad[1]) * ro.xz + 0.5);
        col = HsvToRgb (vec3 (0.1 * (abs (qv4.w) - 20.), ((qv4.w < 0.) ? 1. : 0.6), 1.));
      }
    }
    sh = ObjSShadow (ro, ltDir);
    col = col * (0.2 + 0.1 * max (- dot (vn, ltDir), 0.) + 0.8 * sh * nDotL * nDotL) +
       0.3 * step (0.95, sh) * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
  } else if (rd.y < 0.) {
    ro += dstFlr * rd;
    vn = vec3 (0., 1., 0.);
    if (length (ro.xz) < rad[2]) {
      qv4 = LoadTx ((pFac[2] / rad[2]) * ro.xz + 0.5);
      col = HsvToRgb (vec3 (0.6, ((qv4.w < 0.) ? 1. : 0.4), 1.));         
      vn = VToRMat (vec3 (qv4.yz, 0.).xzy, 0.15 * pi * sign (qv4.x) * (1. -
         smoothstep (0., 0.15, abs (qv4.x)))) * vn;
      sh = ObjSShadow (ro, ltDir);
      nDotL = max (dot (vn, ltDir), 0.);
      col = col * (0.2 + 0.1 * max (- dot (vn, ltDir), 0.) + 0.8 * sh * nDotL) +
         0.2 * step (0.95, sh) * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
      col = mix (col, vec3 (0.3), smoothstep (-0.5, 0., length (ro.xz) - rad[2]));
    } else col = vec3 (0.3);
    att = smoothstep (0.93, 0.97, dot (normalize (8. * ltDir - ro), ltDir));
    col *= att;
  } else {
    col = vec3 (0.);
  }
  if (isRefl) {
    rd = rdr;
    if (dstObjR < dstFar) {
      ro = ror + dstObjR * rd;
      vn = ObjNf (ro);
      nDotL = max (dot (vn, ltDir), 0.);
      nDotL *= nDotL;
      if (idObj == 1) colR = gCol;
      else if (idObj == 2) colR = sCol;
      colR = colR * (0.2 + 0.8 * nDotL * nDotL) +
         0.3 * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
    } else {
      colR = BgCol (rd);
    }
    colR *= att;
    col = mix (col, colR, 0.3);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  nFrame = iFrame;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.1 * pi;
  el = -0.15 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  el = clamp (el, -0.4 * pi, -0.05 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0.5, -8.);
  zmFac = 4.5;
  dstFar = 50.;
  mode = 0;
  ltDir = normalize (vec3 (0.5, 1.2, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

mat3 VToRMat (vec3 v, float a)
{
  mat3 m;
  vec3 w, b1, b2, bp, bm;
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  w = (1. - cs.x) * v * v + cs.x;
  b1 = (1. - cs.x) * v.xzy * v.yxz;
  b2 = - cs.y * v.zyx;
  bp = b1 + b2;
  bm = b1 - b2;
  m[0][0] = w.x;  m[1][1] = w.y;  m[2][2] = w.z;
  m[0][1] = bp.x;  m[1][0] = bm.x;
  m[2][0] = bp.y;  m[0][2] = bm.y;
  m[1][2] = bp.z;  m[2][1] = bm.z;
  return m;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

