// "Aperiodic Penrose Tiling 2" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float Maxv3 (vec3 p);
vec4 LoadTx (vec2 uv);
void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord);

float tCur;
int nFrame;
const float pi = 3.1415927;

#define VAR_ZERO min (nFrame, 0)

bool InTri (vec2 p, vec2 v0, vec2 v1, vec2 v2)
{
  vec3 s;
  vec2 v10, v20, p0;
  float d;
  v10 = v1 - v0;
  v20 = v2 - v0;
  d = v20.y * v10.x - v20.x * v10.y;
  p0 = p - v0;
  s.xy = p0.x * vec2 (v20.y, - v10.y) - p0.y * vec2 (v20.x, - v10.x);
  return (Maxv3 (abs (vec3 (s.xy, d - s.x - s.y) - 0.5 * d)) < 0.5 * abs (d));
}

vec4 EvalTile (vec2 uv)
{
  vec4 qv4;
  vec2 vp[5], csn[5], g, gg, up, vm, v[3];
  float gam[5], gamSum, kCur[5], d, dm, di, ic, f;
  bool fatRom, done;
  const float kLimit = 7.;
  for (int k = 0; k < 5; k ++) csn[k] = sin (2. * pi * float (k) / 5. + vec2 (0.5 * pi, 0.));
  gamSum = 0.;
  for (int k = 0; k < 4; k ++) {
    gam[k] = 0.1 * float (k + 1);
    gamSum += gam[k];
  }
  gam[4] = - gamSum;
  done = false;
  ic = -1.;
  for (int k1 = VAR_ZERO; k1 <= 3; k1 ++) {
    for (int k2 = VAR_ZERO + 1; k2 <= 4; k2 ++) {
      if (k2 >= k1 + 1) {
        fatRom = (k2 - k1 == 1 || k2 - k1 == 4);
        ++ ic;
        gg = gam[k2] * csn[k1] - gam[k1] * csn[k2];
        di = 1. / (csn[k1].x * csn[k2].y - csn[k2].x * csn[k1].y);
        for (float kV1 = - kLimit + float (VAR_ZERO); kV1 <= kLimit; kV1 ++) {
          kCur[k1] = kV1;
          for (float kV2 = - kLimit + float (VAR_ZERO); kV2 <= kLimit; kV2 ++) {
            kCur[k2] = kV2;
            g = (gg + kV1 * csn[k2] - kV2 * csn[k1]).yx * vec2 (1., -1.);
            for (int k = 0; k < 5; k ++) {
              if (k != k1 && k != k2) {
                d = dot (csn[k], g) * di + gam[k];
                d += step (0., d);
                kCur[k] = floor (abs (d)) * sign (d);
              }
            }
            vp[0] = vec2 (0.);
            for (int k = 0; k < 5; k ++) vp[0] += kCur[k] * csn[k];
            vp[2] = vp[0] + csn[k1] + csn[k2];
            vp[1] = vp[2] - csn[k2];
            vp[3] = vp[2] - csn[k1];
            done = (InTri (uv, vp[0], vp[1], vp[2]) || InTri (uv, vp[0], vp[3], vp[2]));
            if (done) break;
          }
          if (done) break;
        }
      }
      if (done) break;
    }
    if (done) break;
  }
  if (done) {
    dm = 99.;
    vp[4] = vp[0];
    for (int k = 0; k < 4; k ++) {
      up = uv - vp[k];
      v[0] = normalize (vp[k + 1] - vp[k]);
      if (k == 0) v[1] = v[0];
      else if (k == 1) v[2] = v[0];
      f = dot (v[0], up);
      d = dot (up, up) - f * f;
      if (d < dm) {
        dm = d;
        vm = v[0];
      }
    }
    qv4.x = sqrt (dm) * sign (v[1].x * v[2].y - v[2].x * v[1].y);
    qv4.yz = vm;
    qv4.w = (ic + 20.) * (fatRom ? -1. : 1.);
  } else qv4 = vec4 (0., 0., 0., -1.);
  return qv4;
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 stDat;
  vec2 pxIv;
  int nfSafe;
  tCur = iTime;
  nFrame = iFrame;
  pxIv = floor (fragCoord);
  if (max (pxIv.x, pxIv.y) >= txSize.x) discard;
  nfSafe = 2;
  if (nFrame == nfSafe || txSize != LoadTx (vec2 (0.)).yz)
     stDat = EvalTile (30. * (fragCoord / txSize  - 0.5));
  else if (nFrame > nfSafe) stDat = LoadTx (fragCoord / txSize);
  else stDat = vec4 (0.);
  if (pxIv == vec2 (0.)) stDat.yz = txSize;
  Savev4 (pxIv, stDat, fragColor, fragCoord);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec4 LoadTx (vec2 uv)
{
  return texture (txBuf, uv);
}

void Savev4 (vec2 vId, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d = abs (fCoord - vId - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

