    int maxSubdivisions = 0;

    // uv = rotate2D(uv,PI/12.0);
    uv.y -= floor(iTime * scrollSpeed * 4.);

    float cellRand;
    vec2 ij;

   	for(int i = 0; i <= maxSubdivisions; i++) { 
        ij = getCellIJ(uv, dims);
        cellRand = random2d(ij);
        dims *= 2.0;
        //decide whether to subdivide cells again
        float cellRand2 = random2d(ij + 454.4543);
        if (cellRand2 > 0.3){
        	break; 
        }
    }

    //draw letters    
    float b = letter(uv, 1.0 / (dims));

    //fade in
    /* float scrollPos = iTime*scrollSpeed + 0.5;
    float showPos = -ij.y + cellRand;
    float fade = smoothstep(showPos ,showPos + 0.05, scrollPos );
    b *= fade; */

    //hide some
    //if (cellRand < 0.1) b = 0.0;

    fragColor = vec4(vec3(1.-b), 1.0);

}

// Fork of "Volumetric laser tracer" by michael0884. https://shadertoy.com/view/NtXSR4
// 2021-07-22 19:27:50

vec3 ColorGrade( vec3 vColor )
{
    vec3 vHue = vec3(1.0, .7, .2);

    vec3 vGamma = 1.0 + vHue * 0.6;
    vec3 vGain = vec3(.9) + vHue * vHue * 8.0;

    vColor *= 1.5;

    float fMaxLum = 100.0;
    vColor /= fMaxLum;
    vColor = pow( vColor, vGamma );
    vColor *= vGain;
    vColor *= fMaxLum;
    return pow(tanh(vColor), vec3(0.57));
}

