const float cDefault[7] = float[](0.,0.,0.,0.,0.,0.,0.);
const float c1[7] = float[](10.,0.,0.,10.,10.,10.,10.);
const float c2[7] = float[](0.,0.,10.,0.,0.,10.,0.);
