const vec2 digitVPos = vec2(0.08,0.073);
const vec2 digitHPos = vec2(0.0,0.15);

float dDigitV(vec2 p){
    float d = B(p,vec2(0.02,0.1));
    p = abs(p);
    p-=vec2(0.02,0.05);
    d = max(Slice(p,44.775),d);
    return d;
}

float dDigitH(vec2 p){
    p*=Rot(radians(90.0));
    float d = dDigitV(p);
    return d;
}

float dDigitBase(vec2 p, float[7] char){
    vec2 prevP = p;
    float d = 10.0;
    float d2 = 10.0;
    // 1
    d = (char[0])+dDigitH(p-digitHPos);
    // 2
    d2 = (char[1])+dDigitV(p-digitVPos);
    d = min(d,d2);
    // 3
    d2 = (char[2])+dDigitV(p+vec2(-digitVPos.x,digitVPos.y));
    d = min(d,d2);
    // 4
    d2 = (char[3])+dDigitH(p+digitHPos);
    d = min(d,d2);
    // 5
    d2 = (char[4])+dDigitV(p+digitVPos);
    d = min(d,d2);
    // 6
    d2 = (char[5])+dDigitV(p+vec2(digitVPos.x,-digitVPos.y));
    d = min(d,d2);
    // 7
    d2 = (char[6])+dDigitH(p);
    d = min(d,d2);

    return d;
}

float dDot(vec2 p){
    p.y = abs(p.y);
    p.y-=0.073;
    float d = B(p,vec2(0.02));
    return d;
}

float dDigitNumber(vec2 p, int num){
    float d = 100.0;
    if(num == 0){
        d = dDigitBase(p,c0);
    } else if(num == 1){
        d = dDigitBase(p,c1);
    } else if(num == 2){
        d = dDigitBase(p,c2);
    } else if(num == 3){
        d = dDigitBase(p,c3);
    } else if(num == 4){
        d = dDigitBase(p,c4);
    } else if(num == 5){
        d = dDigitBase(p,c5);
    } else if(num == 6){
        d = dDigitBase(p,c6);
    } else if(num == 7){
        d = dDigitBase(p,c7);
    } else if(num == 8){
        d = dDigitBase(p,c8);
    } else if(num == 9){
        d = dDigitBase(p,c9);
    }

    return d;
}

float dDrawNumbersBg(vec2 p){
    float d = dDigitBase(p+vec2(0.375,0.0),cDefault);
    float d2 = dDigitBase(p+vec2(0.15,0.0),cDefault);
    d = min(d,d2);
    d2 = dDigitBase(p+vec2(-0.15,0.0),cDefault);
    d = min(d,d2);
    d2 = dDigitBase(p+vec2(-0.375,0.0),cDefault);
    d = min(d,d2);
    return d;
}

float dDrawNumbers(vec2 p){
    float t = iTime;
    float d = dDot(p+vec2(0.0,0.0));

    float d2 = dDigitNumber(p+vec2(0.375,0.0),0);
    d = min(d,d2);
    d2 = dDigitNumber(p+vec2(0.15,0.0),int(mod(t*0.5,10.0)));
    d = min(d,d2);
    d2 = dDigitNumber(p+vec2(-0.15,0.0),int(mod(t,10.0)));
    d = min(d,d2);
    d2 = dDigitNumber(p+vec2(-0.375,0.0),int(mod(t*5.0,10.0)));
    d = min(d,d2);
    return d;
}

vec3 scene(vec2 p, vec3 col){
    p*=2.5;
    p.x+=iTime*0.5;
    vec2 gv = fract(p)-0.5;
    vec2 id = floor(p);

    float n = Hash21(id);
    float d = 100.0;
    if(n<0.6){
        d = B(gv,vec2(0.48));
    }

    col = mix(col,0.5 + 0.5*cos(p.xyx-n+vec3(0,2.+n,3.-n)),S(d,0.0));

    if(d != 100.0){
        gv*=1.5;
        float n2 = (n+n+n+n+n+n)*3.0;
        if(n<0.3){
            gv.x+=((n>=0.1)?1.:-1.0)*iTime*0.3+n2;
            gv.x = mod(gv.x,1.1)-0.55;
            float y = (n>=0.1)?0.5:-0.5;
            float d2 = dDrawNumbersBg(gv-vec2(0.0,y));
            col = mix(col,vec3(0.3),S(max(d,d2),0.0));
            d2 = dDrawNumbers(gv-vec2(0.0,y));
            col = mix(col,vec3(1.0),S(max(d,d2),0.0));
        }

        if(n>=0.3){
            float y = (n<=0.4)?-0.5:0.5;
            gv*=Rot(radians(90.0));
            gv.x+=((n<=0.4)?1.:-1.0)*iTime*0.4+n2;
            gv.x = mod(gv.x,1.1)-0.55;
            float d2 = dDrawNumbersBg(gv-vec2(0.0,y));
            col = mix(col,vec3(0.3),S(max(d,d2),0.0));
            d2 = dDrawNumbers(gv-vec2(0.0,y));
            col = mix(col,vec3(1.0),S(max(d,d2),0.0));
        }
    }


    return col;
}

