vec2 mainSound( int samp, float t )
{
    float t0=t;
    float f1=0.01;
    float s=0.;
    float tact=2.;
    t-=.5*tact;
    float bumm1=(rand(f1*1.*t)+.5*rand(f1*10.*t)+.3*rand(f1*100.*t));
    float bumm2=(rand(f1*2.*t)+.5*rand(f1*20.*t)+.3*rand(f1*100.*t));
    s+=.5*bumm1*AttackDecay(mod(t+ tact* 0.,tact/2.),.05);
    s+=1.*bumm2*AttackDecay(mod(t+ tact/ 4.,tact/2.),.05);
    s+=1.*bumm2*AttackDecay(mod(t+ tact/ 8.,tact*2.),.05);
    s+=1.*bumm2*AttackDecay(mod(t+ tact/16.,tact*2.),.05);
    s+=.25*rand(f1*100.*t)*AttackDecay(mod(t+ tact* 0.,tact/8.),.08);
    int n[16] = int[](-100, 6, 7, 0, -100, 9, 9, 9, 10, -100, 10, 9, -100, 7, 7, -100);	
    t=t0;

    float s1=0.;
    int actTact4=int(t/tact*8.);
    int actTact3=int(t/tact*8.*3./2.);

    for(int i=0;i<2;i++)
    {
    int note=n[(actTact4-i)%16];
    float nStart=(float(actTact4-i)*tact/8.);
    float amp=1.;
    if(note==-100) { note=n[actTact4-i-1]; nStart-=tact/8.; }
    if(note==-100) { note=n[actTact4-i-2]; nStart-=tact/8.; }
    //s=0.;
    float key=110.*exp2(3./12.);
    if(t>tact*4.)
    s1 += amp*string(key*exp2(float(note)/12.),t)*exp(-(t-nStart)/.3);
    }
    //s1= 1.-exp(-s1*3.);

    float ryth=0.;
    for(int j=0;j<CHNUM;j++)
    for(int i=0;i<1;i++)
    {
        int idx=(actTact4-i)%16;
    float f=rythm[idx*CHNUM+j];
    float nStart=(float(actTact4-i)*tact/8.);
    float amp=1.;
    for(int k=0;k<20;k++) { if(f==-1.) { idx=(idx-1)%16; f=rythm[idx*CHNUM+j]; nStart-=tact/8.; } }
    //if(f==-1.) { idx=(idx-1)%16; f=rythm[idx*2+j]; nStart-=tact/4.; }
    //if(f==-1.) { idx=(idx-1)%16; f=rythm[idx*2+j]; nStart-=tact/4.; }
    if(t>tact*4.+1.)
    ryth += amp*clamp(string(f,t),-.25,.25)*exp(-(t-nStart)/.3);
    }
    ryth = overdrive(1.*ryth+2.*s1,50.);

    float sol=0.;
    {
    int i=0;
    int idx=(actTact3-i)%24;
    float f=solo[idx];
    float nStart=(float(actTact3-i)*tact/8.*2./3.);
    float amp=1.;
    for(int j=0;j<20;j++) { if(f==-1.) { idx=(idx-1)%24; f=solo[idx]; nStart-=tact/8.*2./3.; } }
    if(t>tact*4.+1.)
    sol += amp*string(f,t+.001*(sin(t*12.)+.5))*exp(-(t-nStart)/1.);
    }
    sol = overdrive(sol,10.);


    //s = sin(key*1.57*4.*t);
    //s=rand(t);
    return vec2(s*1.+ryth*.25+sol*.25);
}

