float rille2(float d, float w)
{ 
    return w*exp2(-d*d*2./w/w);
}
float rille(float d, float w)
{
    ///// gauss
    //return w*exp2(-d*d*2./w/w);
    ///// exp
    return w*exp2(-abs(d)*1.44/w);
    ///// linear
    //return max(abs(d)-w,0.);
}

