    { th=1.45; ph=-iTime*.25; }
    th=clamp(th,-1.65,1.65);
    q=multQuat(q,axAng2Quat(vec3(0,0,1),ph));
    q=multQuat(q,axAng2Quat(vec3(1,0,0),th));
    pos=transformVecByQuat(pos,q)-vec3(0,0,.2);
