    hit_sphere(spheres[21], r, rec, hit);
    hit_sphere(spheres[22], r, rec, hit);
    hit_sphere(spheres[23], r, rec, hit);

    return hit;
}

bool near_zero(vec3 p) {
    float s = 1e-8;
    return p.x < s && p.y < s && p.z < s;
}

float reflectance(float cosine, float ref_idx) {
    // Use Schlick's approximation for reflectance
    float r0 = (1. - ref_idx) / (1. + ref_idx);
    r0 = r0 * r0;
    return r0 + (1. - r0) * pow((1. - cosine), 5.);
}

void scatter(hit_record rec, ray r, vec2 seed, inout vec3 attenuation, inout ray scattered) {
    material m = rec.material;

    if (m.type == material_lambertian) {
        vec3 scatter_direction = normalize(rec.normal + random_unit_vector(seed));

        // catch degenerate scatter direction
        if (near_zero(scatter_direction)) {
            scatter_direction = rec.normal;
        }

        scattered = ray(rec.p, scatter_direction);
        attenuation = m.albedo;
    } else if (m.type == material_metal) {
        vec3 reflected = reflect(r.dir, rec.normal);
        ray scattered_ = ray(rec.p, normalize(reflected + m.metal_fuzz * random_in_unit_sphere(seed)));
        if (dot(scattered_.dir, rec.normal) > 0.) {
            scattered = scattered_;
            attenuation = m.albedo;
        }
    } else if (m.type == material_dielectric) {
        bool front_face = dot(r.dir, rec.normal) < 0.;
        vec3 adjusted_normal = front_face ? rec.normal : -rec.normal;
        float ref = m.dielectric_index_of_refraction;
        float refraction_ratio = front_face ? 1.0/ref : ref;

        float cos_theta = min(dot(-r.dir, adjusted_normal), 1.0);
        float sin_theta = sqrt(1.0 - cos_theta * cos_theta);

        bool cannot_refract = refraction_ratio * sin_theta > 1.0;
        vec3 direction;

        if (cannot_refract || reflectance(cos_theta, refraction_ratio) > hash12(seed)) {
            direction = reflect(r.dir, adjusted_normal);
        } else {
            direction = refract(r.dir, adjusted_normal, refraction_ratio);
        }

        scattered = ray(rec.p, direction);
        attenuation = vec3(1);
    }
}

vec3 ray_color(in ray r, vec2 seed) {
    vec3 color = vec3(1);
    hit_record rec;
    int depth;
    for (depth = 0; depth < MAX_RAY_BOUNCES; depth++) {
        if (hit(r, rec)) {
            ray scattered;
            vec3 attenuation;
            scatter(rec, r, seed * 999. + float(depth), attenuation, scattered);
            r = scattered;
            color *= attenuation;
        } else {
            // hit bg, aka nothing
            float t = 0.5 * (r.dir.y + 1.0);
            color *= mix(vec3(1.), vec3(0.5, 0.7, 1.0), t);

            break;
        }
    }

    if (depth == MAX_RAY_BOUNCES) {
        return vec3(0);
    }
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // camera
    vec3 lookfrom = vec3(cos(iTime) * 13., 2.0, sin(iTime) * 10.);
    vec3 lookat = vec3(0, 0, 0);
    vec3 vup = vec3(0, 1, 0);
    float vfov = 30.0;
    float aspect_ratio = iResolution.x / iResolution.y;
    float aperture = 0.1;
    float focus_dist = 10.0;

    float theta = radians(vfov);
    float h = tan(theta / 2.);
    float viewport_height = 2.0 * h;
    float viewport_width = aspect_ratio * viewport_height;

    vec3 w = normalize(lookfrom - lookat);
    vec3 u = normalize(cross(vup, w));
    vec3 v = cross(w, u);

    vec3 origin = lookfrom;
    vec3 horizontal = focus_dist * viewport_width * u;
    vec3 vertical = focus_dist * viewport_height * v;
    vec3 lower_left_corner = origin - horizontal / 2. - vertical / 2. - focus_dist * w;

    float lens_radius = aperture / 2.;

    // render
    vec3 color = vec3(0);
    for (float s = 0.; s < SAMPLES_PER_PIXEL; s++) {
        vec2 rand = hash22(fragCoord * 999. + s + iTime);

        vec2 normalizedCoord = (fragCoord + rand) / iResolution.xy;
        vec3 rd = lens_radius * random_in_unit_disk(normalizedCoord * 999. + s + iTime);
        vec3 offset = u * rd.x + v * rd.y;
        ray r = ray(
            origin + offset,
            normalize(lower_left_corner + normalizedCoord.x * horizontal + normalizedCoord.y * vertical - origin - offset)
        );
        color += ray_color(r, normalizedCoord);
    }

    fragColor = vec4(sqrt(color / SAMPLES_PER_PIXEL), 1.0);
}

// See https://www.desmos.com/calculator/l9ipcrouvp
// I feel like this should be simpler

// My process:
// 1. Split the triangle into to wedge segments that meet at on a horizontal line
// 2. Integrate a slice of each wedge along the x axis (bounds vary linearly with the y coordinate)
// 3. Integrate along the height of the wedge segments in the y axis
// 4. Combine the inertia for both segments

// SDFs
float sdLine(in vec2 p, in vec2 a, in vec2 b, in float t) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0)) - t;
}

float sdTriangle(in vec2 p, in vec2 a, in vec2 b, in vec2 c) {
    vec2 ba = b - a, cb = c - b, ac = a - c;
    vec2 pa = p - a, pb = p - b, pc = p - c;

    float abc = ba.x * cb.y - ba.y * cb.x;
    float abp = ba.x * pa.y - ba.y * pa.x;
    float bcp = cb.x * pb.y - cb.y * pb.x;
    float cap = ac.x * pc.y - ac.y * pc.x;

    vec2 ae = pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    vec2 be = pb - cb * clamp(dot(pb, cb) / dot(cb, cb), 0.0, 1.0);
    vec2 ce = pc - ac * clamp(dot(pc, ac) / dot(ac, ac), 0.0, 1.0);

    float tri = sqrt(min(dot(ae, ae), min(dot(be, be), dot(ce, ce))));
    return -tri * sign(min(abp, min(bcp, cap)) * max(abp, max(bcp, cap)));
}

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    float ang = texelFetch(iChannel0, ivec2(0), 0).x;
    float co = cos(ang), si = sin(ang);
    mat2 rot = mat2(co, si, -si, co);

    vec2 a = A * rot;
    vec2 b = B * rot;
    vec2 c = C * rot;

    float tri = sdTriangle(uv, a, b, c);
    drawSDF(tri, vec3(1.0, 0.0, 0.0));
    drawSDF(abs(tri), vec3(1.0, 0.8, 0.0));
    drawSDF(length(uv) - 0.05, vec3(1.0, 0.8, 0.0));

    float aspect = iResolution.x / iResolution.y;
    color = digitIn(color, vec3(1.0), uv * 0.25 + vec2(0.45 * aspect, 0.45), 100.0, triangleInertia(A, B, C));

    fragColor = vec4(color, 1.0);
}

