// Triangle settings
#define SCALE (iTime > 13.0 ? 1.0 + 0.5 * cos(iTime - 13.0 - 1.57) : 1.0)
#define A (vec2(0.799, 2.088) * SCALE)
#define B (vec2(-1.075, -0.307) * SCALE)
#define C (vec2(0.405, -0.442) * SCALE)
#define DENSITY 0.1

// Simulation settings
#define FRICTION 0.98
#define GRAVITY 0.2

// cross3D(vec3(a.x, a.y, 0), vec3(b.x, b.y, 0))
// Since the x and y components of the result are always zero
// The z component is the only one of interest
float cross2D(in vec2 a, in vec2 b) {
    return a.x * b.y - a.y * b.x;
}

float triangleMass(in vec2 a, in vec2 b, in vec2 c, in float density) {
    return 0.5 * abs(cross2D(b - a, c - a)) * density;
}

// Moment of inertia of a triangle with vertices a, b, and c
// The rotation axis is assumed to be at (0, 0). You can change
// it by translating the triangle's vertices. Obviously, horizontal
// and vertical edges pose an issue, this can be avoided by nudging
// the vertices or rotating them about (0, 0) (such a rotation will
// not change the moment of inertia)
#define pow3(x) x * x * x
#define pow4(x) x * x * x * x
#define swap(a, b) { tmp = a, a = b, b = tmp; }
float triangleInertia(in vec2 a, in vec2 b, in vec2 c) {
    vec2 low = a, mid = b, high = c, tmp; // Sort vertices by y coordinate
    if (mid.y < low.y) swap(low, mid);
    if (high.y < mid.y) {
        if (high.y < low.y) swap(low, high);
        swap(mid, high);
    }

    vec2 mid2 = low + (high - low) * (mid.y - low.y) / (high.y - low.y); // Point on the edge across from the middle vertex in y
    vec4 split = mid2.x < mid.x ? vec4(mid2, mid) : vec4(mid, mid2);

    // Lower wedge segment (1 is left, 2 is right) ---> an*y+bn
    float a1 = (split.x - low.x) / (split.y - low.y);
    float b1 = low.x - (split.x - low.x) / (split.y - low.y) * low.y;
    float a2 = (split.z - low.x) / (split.w - low.y);
    float b2 = low.x - (split.z - low.x) / (split.w - low.y) * low.y;

    // Upper wedge segment (3 is left, 4 is right) ---> an*y+bn
    float a3 = (high.x - split.x) / (high.y - split.y);
    float b3 = split.x - (high.x - split.x) / (high.y - split.y) * split.y;
    float a4 = (high.x - split.z) / (high.y - split.w);
    float b4 = split.z - (high.x - split.z) / (high.y - split.w) * split.w;

    // Combine both segments' moments of inertia
    float I  = (1.0 / a2 - 1.0 / a4) * pow4(split.z) + (1.0 / a3 - 1.0 / a1) * pow4(split.x);
          I += (1.0 / a1 - 1.0 / a2) * pow4(  low.x) + (1.0 / a4 - 1.0 / a3) * pow4( high.x);

    I += 3.0 * ((a2 - a1) * (pow4(mid.y) - pow4(low.y)) + (a4 - a3) * (pow4(high.y) - pow4(mid.y)));
    I += 4.0 * ((b2 - b1) * (pow3(mid.y) - pow3(low.y)) + (b4 - b3) * (pow3(high.y) - pow3(mid.y)));

    return I / 12.0;
}

