// The minimum implementation of the distance 2D utilities for private use.
// I guess most of the stuff is similar implementation someone has done.
// The following distance functions can not use the round shape technique and etc...
#define USE_MOUSE 0
#define S(p,d,b) smoothstep(fwidth(p.y),b,d)
#define M() (iMouse.xy-0.5*iResolution.xy)/iResolution.y
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define Tri(p,s,a) max(-dot(p,vec2(cos(-a),sin(-a))),max(dot(p,vec2(cos(a),sin(a))),max(abs(p).x-s.x,abs(p).y-s.y)))
#define Slice(p,a) dot(p,vec2(cos(a),sin(a)))

#define SliceB(p,a,b,s) max(-dot(p-a,vec2(cos(b),sin(b))),max(abs(p).x-s,abs(p).y-s))
float customTri(vec2 p, vec2 a, vec2 b, vec2 c){
    float s = 1.0;
    float d = max(SliceB(p,b,-atan(b.x-a.x,b.y-a.y),s),SliceB(p,b,-atan(c.x-b.x,c.y-b.y),s));
    return max(SliceB(p,c,-atan(a.x-c.x,a.y-c.y),s),d);
}

float bevelCornerBox(vec2 p, vec2 s, float k, float deg){
    float d = max(abs(p).x-s.x,abs(p).y-s.y);
    float a = radians(deg);
    p = abs(p);
    p-=s/k;
    d = max(dot(p,vec2(cos(a),sin(a))),d);
    return d;
}

// taper box
float taperBox(vec2 p, vec2 s, float l, float r){
    float d = max(abs(p).x-s.x,abs(p).y-s.y);
    float al = radians(-l);
    float ar = radians(r);
    d = max(-dot(p-vec2(-s.x*0.5,0.0),vec2(cos(al),sin(al))),d);
    d = max(dot(p-vec2(s.x*0.5,0.0),vec2(cos(ar),sin(ar))),d);
    return d;
}

// I won't recommend using the following distance 2d line. This implementation is straight foward. Please use the Iq's sdLine is the best one.
// This one is for me to use. I just wanted to create a distance line by myself.
float dLine(vec2 p, vec2 a, vec2 b, float w){
    vec2 v = normalize(b-a);
    vec2 right = normalize(cross(vec3(v,0.0),vec3(0.0,0.0,1.0)).xy);
    vec2 newRightVec = right*(w*0.5);
    vec2 a1 = a+newRightVec, a2 = a-newRightVec, b1 = b+newRightVec, b2 = b-newRightVec;
    vec2 mida = vec2((a1.x + b1.x) * 0.5, (a1.y + b1.y) * 0.5);
    vec2 midb = vec2((a2.x + b2.x) * 0.5, (a2.y + b2.y) * 0.5);

    float rad1 = -atan(b1.x-a1.x,b1.y-a1.y);
    float rad2 = -atan(a2.x-a1.x,a2.y-a1.y);

    float mad = Slice(p-mida,rad1);
    float mbd = Slice(p-midb,rad1);

    float ad = Slice(p-a,rad2);
    float bd = Slice(p-b,rad2);
    float d = max(max(mad,-mbd),max(-ad,bd));
    return d;
}

float drawExample(vec2 p){
    // i.e. draw circle
    float d = length(p-vec2(-0.75,0.0))-0.1;

    // i.e. draw box
    float d2 = B(p-vec2(-0.5,0.0),vec2(0.1));
    d = min(d,d2);
    d2 = B(p-vec2(-0.3,0.0),vec2(0.05,0.1));
    d = min(d,d2);

    // i.e. draw triangle
    d2 = Tri(p-vec2(-0.09,0.1),vec2(0.2),radians(30.0));
    d = min(d,d2);

    d2 = customTri(p,vec2(0.15,0.1),vec2(0.3,0.0),vec2(0.08,-0.1));
    d = min(d,d2);

    // i.e. draw bevel corner box
    d2 = bevelCornerBox(p-vec2(0.45,0.0),vec2(0.1,0.1),1.3,45.0);
    d = min(d,d2);

    // i.e. draw taper box
    d2 = taperBox(p-vec2(0.72,0.0),vec2(0.2,0.1),-10.0,-10.0);
