#define D(p) abs(dot(sin(p), cos(p.yzx)))

vec2 mp = vec2(0);

mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

vec3 hsv(float h, float s, float v) {
    vec4 a = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + a.xyz) * 6.0 - vec3(a.w));
    return v * mix(vec3(a.x), clamp(p - vec3(a.x), 0.0, 1.0), s);
}

float map(vec3 p) {
    float d = length(p) - 1.8 + mp.x;
    p *= 10.;
    d = max(d, (D(p) - .08) / 10.);
    p *= 10.;
    d = max(d, (D(p) - .4 - 1.1*mp.y) / 100.);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
