// SDFs
float sdLine(in vec2 p, in vec2 a, in vec2 b, in float t) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0)) - t;
}

float sdBox(in vec2 p, in vec2 b) {
    p = abs(p) - b;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y));
}

float sdIsosceles(in vec2 p, in float b, in float h) {
    p.x = abs(p.x);
    float q = clamp((b - p.x + p.y) / (b + h), 0.0, 1.0);
    vec2 re = vec2(p.x - b + b * q, p.y - h * q);
    vec2 be = vec2(p.x - b * min(p.x / b, 1.0), p.y);
    return sqrt(min(dot(re, re), dot(be, be))) * sign(p.x + (p.y - h) * b / h * sign(p.y));
}

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y + vec2(0.0, 0.5 * BUCKET_SIZE.y);
    float unit = 2.0 / iResolution.y;
