    // Wheel spokes
    float wheelAngle = texelFetch(iChannel0, ivec2(0, 1), 0).x;
    float co = cos(wheelAngle), si = sin(wheelAngle);
    vec2 wheelUv = uv * mat2(co, si, -si, co);
    float ang = mod(atan(wheelUv.y, wheelUv.x) + PI / BUCKETS, TAU / BUCKETS) - PI / BUCKETS;
    wheelUv = vec2(cos(ang), sin(ang)) * length(wheelUv) - vec2(0.5 * WHEEL_RADIUS, 0.0);
    drawSDF(sdBox(wheelUv, vec2(WHEEL_RADIUS, SPOKE_WIDTH) * 0.5), vec3(0.75, 0.4, 0.0));

    float mask = 1e20;
    vec2 maskPos = vec2(0.0);
    for (float n=0.0; n < BUCKETS; n++) {
        float bucket = texelFetch(iChannel0, ivec2(n, 0), 0).x;
        vec2 waterLevel = BUCKET_SIZE * clamp(bucket, 0.001, 0.999); // clamp(0.001, 0.999) to avoid divide by zero in sdIsosceles()
        float ang = TAU * n / BUCKETS + wheelAngle;
        vec2 pos = vec2(cos(ang), sin(ang)) * WHEEL_RADIUS;

        // Check before setting it as the spout mask
        float shape = sdIsosceles(pos - uv + vec2(0.0, BUCKET_SIZE.y), BUCKET_SIZE.x, BUCKET_SIZE.y);
        if (pos.x > -BUCKET_SIZE.x && pos.x < BUCKET_SIZE.x && pos.y > 0.0 && shape < mask) {
            mask = shape;
            maskPos = pos;
        }

        // Draw the bucket
        drawSDF(shape, vec3(0.5));
        drawSDF(sdIsosceles(pos - uv + vec2(0.0, waterLevel.y), waterLevel.x, waterLevel.y), vec3(0.0, 1.0, 1.0));
    }

    if (mask < 1e10) drawSDF(max(sdBox(uv - vec2(0.0, 0.55), vec2(0.01, 1.0)), min(mask, maskPos.y + BUCKET_SIZE.y - uv.y)), vec3(0.0, 1.0, 1.0));
    drawSDF(sdBox(uv - vec2(0.0, 0.55), vec2(0.02, 0.025)), vec3(0.5));

    fragColor = vec4(color, 1.0);
}

