// Simulation
void mainImage(out vec4 data, in vec2 addr) {
    data = vec4(0.0);
    addr -= 0.5; // Once again I am reminded that fragment coordinates weirdly go from (0.5, 0.5) to iResolution.xy+0.5
    ivec2 iAddr = ivec2(addr);
    if (iFrame == 0) {
        if (iAddr.x == 0 && iAddr.y == 1) data.x = 0.05;
        if (iAddr.x == 2 && iAddr.y == 0) data.x = 0.75;
    }

    if (iFrame > 0) {
        data = texelFetch(iChannel0, iAddr, 0);
        if (addr.x < BUCKETS && iAddr.y == 0) {
            float ang = TAU * addr.x / BUCKETS + texelFetch(iChannel0, ivec2(0, 1), 0).x;
            vec2 pos = vec2(cos(ang), sin(ang)) * WHEEL_RADIUS;

            // Fill and drain
            if (pos.x > -BUCKET_SIZE.x && pos.x < BUCKET_SIZE.x && pos.y > 0.0) data.x = min(1.0, data.x + FILL_RATE);
            else data.x = max(0.0, data.x - DRAIN_RATE);
        }

        if (iAddr == ivec2(0, 1)) {
            float torque = 0.0; // Angular equivalent of force
            for (float n=0.0; n < BUCKETS; n++) {
                float bucket = texelFetch(iChannel0, ivec2(n, 0), 0).x;
                float ang = TAU * n / BUCKETS + data.x;
                float mass = 0.5 * BUCKET_SIZE.x * BUCKET_SIZE.y * bucket * bucket * FLUID_DENSITY;
                //vec2 force = vec2(0.0, -mass * GRAVITY);
                //float tangentialForce = force.y * cos(ang) - force.x * sin(ang); // Project the force onto the spoke
                float tangentialForce = -mass * GRAVITY * cos(ang);
                torque += tangentialForce * WHEEL_RADIUS;
            }

            data.y += torque / WHEEL_INERTIA; // Angular equivalent to accel=force/mass
            data.y *= WHEEL_FRICTION;
            data.x += data.y;
        }
    }
}

