//HASH BY DAVE HOSKINS
//https://www.shadertoy.com/view/4djSRW
vec3 rnd(vec3 p3) {
    p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz + 33.33);
    return fract((p3.xxy + p3.yxx) * p3.zyx);

}

float noise(vec3 z) {
    float x = texture(iChannel0, z * .1).r * 2.;
    x += texture(iChannel0, z * .2).r;
    x += texture(iChannel0, z * .4).r * .7;
    x += texture(iChannel0, z * .8).r * .4;
    x *= 0.6;
    return x;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    vec3 col = vec3(0.);
    vec3 inc = normalize(vec3(uv, .5));
    vec3 contribution = vec3(1.);
    vec3 radt = vec3(0.);
    float dmag = 0.0001;
    vec3 dz = normalize(vec3(1., 1., -.5)) * dmag;

    float rl = 0.;
    for(int i = 0; i < 100; i++) {
        rl += 0.02;
        vec3 z = inc * rl - vec3(0., 0., 2.);
        float density = max(.5 - dot(z, z) + noise(z), 0.) * .15;
        vec3 l = normalize(rnd(z * 1000. + iTime) - 0.5);
        dz = l * dmag;
        float dens2 = max(.5 - dot(z + dz, z + dz) + noise(z), 0.) * .15;
        vec3 t = texture(iChannel1, l).rgb;
        t = pow(t, vec3(5.));
        vec3 chd = max((density - dens2) / dmag, 0.) * t * 200.;
        vec3 rad = chd;
        float d = exp(-density);
        contribution *= d;
        radt += contribution * rad * density;
    }

    vec3 tex = texture(iChannel1, inc).rgb;
    tex *= tex;
    col = contribution * tex + radt;
    col *= 3.;
    col = col / (col + 1.);
    col = smoothstep(0., 1., col);
    col = sqrt(col);

    fragColor = (vec4(col, 1.0) + texture(iChannel2, fragCoord / iResolution.xy) * float(iFrame)) / (float(iFrame) + 1.);
}

