float polarStar( in vec2 p )
{
    const float pi5 = 0.628318530718; // pi/5
    const float ph2 = 3.2360679775; // 2 * phi

    float m2 = mod(atan(p.y, p.x)/pi5 + 1.0, 2.0);

    return ph2 * length(p) * cos(pi5 * (m2 - 4.0 * step(1.0, m2) + 1.0)) - 1.0;
}

float polarStar2( in vec2 p )
{
    const float pi5 = 0.628318530718; // pi/5
    const float ph2 = 3.2360679775; // 2 * phi

    float m2 = mod(atan(p.y, p.x)/pi5 + 1.0, 2.0);

    return ph2 * length(p) * cos(pi5 * (m2 - 4.0 * step(1.0, m2) + 1.0)) - 1.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coords
    float px = 2.0/iResolution.y;
	vec2 uv = (2.0 * fragCoord - iResolution.xy)/iResolution.y;

    float uoffset = 0.00;

    float t  = (0.75 - uoffset) * iTime * sign(-uv.x) ;
    float t2 = 0.75 * iTime * sign(-uv.x) ;
    float t3 = (0.75 + uoffset) * iTime * sign(-uv.x) ;

    float middis = max((1.0 - abs( uv.x * 0.5)) * 2.5 , 1.1 ) ;
    //middis = 0.1;

    vec2 q = uv * vec2(2,2) + vec2(t,0);
    float d = polarStar2( (fract(q) - vec2(0.5)) * vec2( 5.0,5.0 ) * middis );

    vec2 q2 = uv * vec2(2,2) + vec2(t2,0) + vec2(0.7,0.0);
    float d2 = polarStar2( (fract(q2) - vec2(0.5)) * vec2( 5.0,5.0 )* middis );

    vec2 q3 = uv * vec2(2,2) + vec2(t3,0) + vec2(0.3,0.0);
    float d3 = polarStar2( (fract(q3) - vec2(0.5)) * vec2( 5.0,5.0 )* middis );

    d = step(d,0.8);
    d2 = step(d2,0.8);
    d3 = step(d3,0.8);
    //d += d2 + d3;

    d = max( max(d,d2),d3);

    vec3 col = mix(vec3(1.0, 1.0, 1.0), vec3(1.2, 1.4, 1.6), smoothstep(-2.0 * px, 2.0 * px, d));
	col *= smoothstep(0.02, 0.02 + 2.0 * px, abs(d));
    fragColor = vec4( col, 1.0 );

   // vec3 col = vec3(d,d,d);
    col = mix(vec3(0.0, 0.0, 0.0), vec3(1.0, 1.0, 1.0), smoothstep(-2.0 * px, 2.0 * px, d));
    //col = mix(vec3(0.0, 0.0, 0.0), vec3(1.0, 1.0, 1.0), d);
    col *= smoothstep(0.02, 0.02 + 2.0 * px, abs(d));
	//fragColor = vec4( col, 1.0 );
  //  fragColor = vec4( fract( q.y) );
    //fragColor = vec4( uv.x  );
}

const float PI = 3.1415926536;

