#define AA  0   // (= 0/1) optional antialiasing

#define VAR_ZERO min (iFrame, 0)

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrBox2Df (vec2 p, vec2 b);
float PrRoundBox2Df (vec2 p, vec2 b, float r);
float PrCylDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float PrCapsDf (vec3 p, float r, float h);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Hashv2f (float p);

vec3 qHit,ltPos, cPos;
vec2 gCur, gSize, cDel, angC;
float dstFar, tCur, tCyc, tPhs, hCyc, bWid, zPos;
int idObj;
bool isSh;
const int idCyl = 1, idTel = 2, idRail = 3, idFrame = 4, idBase = 5, idWal = 6, idSup = 7, idLamp = 8,
   idCylP = 9;
const float pi = 3.1415927;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, ts, zFrm;
  dMin = dstFar;
  zFrm = 3. * 0.9 + 0.96;
  zPos = 0.;
  for (float k = float (VAR_ZERO); k <= 3.; k ++) {
    q = p;
    q.xy -= vec2 (0.5 * (gCur.x + cDel.x), cDel.y);
    if (k > 0.) zPos += 0.1 + 0.9 * SmoothBump (0.3 + 0.03 * k, 0.7 - 0.03 * k, 0.03, tPhs);
    q.xz = Rot2D (q.xz, angC.x);
    q.yz = Rot2D (q.yz, angC.y);
    q.z -= 0.9 * zPos;
    d = PrRoundCylDf (q, 0.2 - 0.03 * k, 0.02, 0.5);
    DMINQ (idTel);
  }
  ts = abs (tPhs - 0.5) - 0.42;
  if (hCyc == 0. || ts < 0.) cPos.xy = vec2 (0.5 * gCur.x, (hCyc == 0.) ? 0.5 * gCur.y : cDel.y);
  else cPos.xy = vec2 (0.5 * gCur.x + 2. * bWid * smoothstep (0., 0.08, ts) * sign (tPhs - 0.5),
     -0.25 * gSize.y - 2. + 0.5);
  q = p;
  if (hCyc == 0. || ts < 0.) {
    q.xy -= cPos.xy;
    if (hCyc != 0.) {
      q.xz = Rot2D (q.xz, angC.x);
      q.yz = Rot2D (q.yz, angC.y);
      q.z -= 0.9 * (zPos - 3.);
    }
    q.z -= zFrm;
    d = PrRoundCylDf (q, 0.15, 0.02, 0.41);
  } else {
    d = bWid - abs (q.x);
    q.xy -= cPos.xy;
    q = q.xzy;
    d = max (PrRoundCylDf (q, 0.15, 0.02, 0.4), - d);
  }
  DMINQ (idCylP);
  q = p;
  d = max (PrBox2Df (q.xy, 0.25 * gSize), - PrBox2Df (q.xy - 0.5 * gCur, vec2 (0.25)));
  q.xy = mod (q.xy + 0.25, 0.5) - 0.25;
  q.z -= zFrm;
  d = max (d, PrRoundCylDf (q, 0.15, 0.02, 0.4));
  DMINQ (idCyl);
  q = p;
  q.x -= 0.5 * (gCur.x + cDel.x);
  q.y -= cDel.y;
  q.xz = Rot2D (q.xz, angC.x);
  d = PrRoundCylDf (q.yzx, 0.03, 0.02, 0.45);
  DMINQ (idRail);
  q = p;
  q.x -= 0.5 * (gCur.x + cDel.x);
  q.xz = Rot2D (q.xz, angC.x);
  q.y -= 0.25 * gSize.y + 0.5;
  d = min (PrCylDf (vec3 (q.xz, q.y - 0.65), 0.12, 0.9), PrCapsDf (vec3 (q.xz, abs (q.y + 0.05) - 0.2).yzx,
     0.12, 0.4));
  q.x = abs (q.x) - 0.4;
  q.y -= -0.25 * gSize.y - 0.5;
  d = min (d, SmoothMax (PrCapsDf (q.xzy, 0.12, 0.25 * gSize.y + 0.65),
     - PrRoundBoxDf (q - vec3 (-0.08, -0.2, 0.), vec3 (0.08, 0.25 * gSize.y + 0.2, 0.04), 0.01), 0.02));
  DMINQ (idRail);
  q = p;
  q.z = abs (q.z - zFrm) - 0.3;
  d = max (PrRoundBoxDf (q, vec3 (0.25 * gSize + 0.25, 0.05) - 0.05, 0.05),
     - max (PrBox2Df (q.xy, vec2 (0.25 * gSize)), length (mod (q.xy + 0.25, 0.5) - 0.25) - 0.17));
  DMINQ (idFrame);
  q = p;
  q.xy = abs (q.xy);
  q -= vec3 (0.25 * gSize.xy + vec2 (0.1, 1.1), zFrm);
  d = PrRoundCylDf (q.xzy, 0.4, 0.02, 0.9);
  DMINQ (idSup);
  if (! isSh) {
    q = p;
    q.x = abs (q.x);
    q -= vec3 (bWid, -0.25 * gSize.y - 0.98, 2.);
    d = max (PrRoundBoxDf (q, vec3 (0.25, 1.2, 5.) - 0.02, 0.02), - SmoothMax (q.x - 0.1,
       PrBox2Df (q.yz - vec2 (-0.4, -2.), vec2 (0.6, 0.3)), 0.02));
    DMINQ (idWal);
    q = p;
    q.y = abs (q.y);
    q.yz -= vec2 (0.25 * gSize.y + 2.08, 2.);
    d = PrRoundBoxDf (q, vec3 (bWid + 0.25, 0.1, 5.) - 0.02, 0.02);
    DMINQ (idBase);
    q = p - ltPos;
    d = PrCapsDf (q.yzx, 0.16, 0.3);
    DMINQ (idLamp);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0005, -0.0005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float ltDist)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > ltDist) break;
  }
  return 0.5 + 0.5 * sh;
}

vec4 ObjCol (vec3 ro)
{
  vec4 col4;
  float s;
  if (idObj == idCyl || idObj == idCylP) {
    col4 = vec4 (0.2, 0.7, 1., 0.2) * (1. - 0.3 * smoothstep (0., 0.02, abs (abs (qHit.z) - 0.2)) - 0.1) *
       (0.6 + 0.4 * smoothstep (0., 0.02, length (qHit.xy) - 0.05));
    if (abs (qHit.z) < 0.2) col4 *= (1. + 0.3 * smoothstep (0.4, 0.5, sin (16. * atan (qHit.y, - qHit.x))));
    if (idObj == idCylP && hCyc != 0. && zPos < 2.99 && abs (qHit.z) > 0.3)
       col4 = mix (col4, vec4 (1., 0., 0., -1.), step (0.5, mod (4. * tCur, 1.)));
  } else if (idObj == idTel) {
    col4 = vec4 (0.8, 0.85, 0.8, 0.2) *
       (1. - 0.2 * smoothstep (0., 0.05, abs (qHit.z) - 0.4));
  } else if (idObj == idRail) {
    col4 = vec4 (0.85, 0.85, 0.9, 0.2);
  } else if (idObj == idFrame) {
    col4 = vec4 (0.6, 0.6, 0.4, 0.) * (1. - 0.4 * smoothstep (0., 0.02,
       PrRoundBox2Df (qHit.xy, vec2 (0.25 * gSize) - 0.1, 0.2)));
    if (cDel.y == 0.5 * gCur.y && length (qHit.xy - cPos.xy) < 0.22) col4 = vec4 (0., 1., 0., -1.);
  } else if (idObj == idSup) {
    col4 = vec4 (0.6, 0.6, 0.4, 0.1);
    if (length (qHit.xz) > 0.35) col4 *= 0.8 + 0.2 * smoothstep (-0.4, -0.2,
       sin (16. * atan (qHit.z, - qHit.x)));
  } else if (idObj == idBase) {
    col4 = vec4 (0.5, 0.3, 0.1, 0.);
    if (abs (ro.x) < bWid + 0.2 && abs (ro.y) < 0.25 * gSize.y + 2.1) {
      col4 *= 0.6 + 0.4 * smoothstep (0., 0.03, abs (abs (qHit.z + 2.) - 0.2) - 0.02);
      if (abs (qHit.z + 2.) < 0.2) col4 *= 0.6 + 0.4 * smoothstep (0., 0.03,
         abs (mod (qHit.x, 0.5) - 0.25) - 0.02);
    }
  } else if (idObj == idWal) {
    col4 = vec4 (0.5, 0.3, 0.1, 0.);
    if (abs (ro.x) < bWid + 0.2) {
      s = (1. - smoothstep (0., 0.02, abs (qHit.z + 2.) - 0.3)) *
         (1. - smoothstep (0., 0.02, abs (qHit.y + 0.4) - 0.6));
      if (s > 0. && sign (ro.x) * sign (cPos.x - 0.5 * gCur.x) > 0. && abs (ro.x) > bWid - 0.15)
         col4 = vec4 (1., 0., 0., -1.);
      else col4 *= 1. - 0.2 * s;
    }
  } else if (idObj == idLamp) {
    col4 = vec4 (vec3 (0.9, 0.9, 0.8) * (0.95 + 0.05 * cos (64. * pi * qHit.y)), -1.);
  }
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, ltDir;
  float dstObj, tf, ltDist, sh, nDotL;
  gSize = vec2 (13., 9.);
  bWid = 4.5;
  tCyc = 10.;
  tCur += 2. * tCyc;
  tPhs = mod (tCur / tCyc, 1.);
  hCyc = floor (mod (tCur / tCyc + 0.5, 2.));
  tf = floor (tCur / (2. * tCyc));
  gCur = - floor (gSize / 2.) + floor (gSize * Hashv2f (17.11 * tf));
  angC.y = (hCyc != 0.) ? -0.5 * pi *  (1. - SmoothBump (0.15, 0.85, 0.05, tPhs)) : 0.;
  angC.x = angC.y;
  cDel.x = (floor (gSize.x * Hashv2f (17.11 * (tf - 1.)).x - floor (gSize.x / 2.)) - gCur.x) *
     (1. - smoothstep (0., 1.5, tCur - 2. * tCyc * tf));
  cDel.y = (0.5 * gCur.y + 2.5) * SmoothBump (0.15, 0.85, 0.06, tPhs) - 2.5;
  ltPos = vec3 (0., 0.25 * gSize.y + 1.8, -2.6);
  isSh = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = ObjCol (ro);
    ltDir = ltPos - ro;
    ltDist = length (ltDir);
    ltDir /= ltDist;
    nDotL = max (dot (vn, ltDir), 0.);
    if (idObj == idRail || idObj == idTel) nDotL *= nDotL;
    isSh = true;
    sh = ObjSShadow (ro + 0.01 * vn, ltDir, ltDist);
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.8 * sh * nDotL) +
       step (0.95, sh) * col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    else col = col4.rgb * (0.5 - 0.5 * dot (rd, vn));
  } else {
    col = vec3 (0.1, 0.1, 0.15);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.05 * pi;
  if (mPtr.z > 0.) {
    az += 1.5 * pi * mPtr.x;
    el += 0.3 * pi * mPtr.y;
  } else {
    az = 0.2 * pi * (2. * mod (floor (0.05 * tCur), 2.) - 1.) *
       SmoothBump (0.3, 0.7, 0.15, mod (0.05 * tCur, 1.));
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0.5, -18.);
  ro.y -= 0.5;
  zmFac = 3.5;
  dstFar = 100.;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

