void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (gl_FragCoord.xy - .5 * u_resolution.xy) / u_resolution.y;
  vec2 mouse = (u_mouse.xy - 0.5 * u_resolution.xy) / u_resolution.y;
  float t = u_time / 1.0;
  vec3 col = BLACK;
  uv *= 4.0;

  float fw = max(fwidth(uv.x), fwidth(uv.y));

  float grow_shrink = growShrink();
  //GROW_SHRINK * sin(t * PI) * 0.1 + 0.9;

  mat4 boxTrans = rot4(mouse.x * TAU / 2.0, 4) * rot4(mouse.y * TAU / 2.0 + .3, 1);
  boxTrans *= rot4(t * 13.0 / 17.0 * ROTATE_IDLE + PI / 3.0 + .9, 0) * rot4(t * 13.0 / 11.0 * ROTATE_IDLE - 17.0 * PI / 7.0, 2);
  boxTrans *= rot4(t * 13.0 / 17.0 * ROTATE_IDLE + PI - .2, 2) * rot4(t * 13.0 / 11.0 * ROTATE_IDLE - 12.0 * PI / 7.0 - .2, 3);
  boxTrans *= rot4(t * 13.0 / 17.0 * ROTATE_IDLE + PI / 1.0, 4) * rot4(t * 9.0 / 11.0 * ROTATE_IDLE - 7.9 * PI / 7.0, 5);

  // raymarch over the orthogonal scene
  // vec2 sc = sinCos(vec2(t));
  vec2 an = sinCos(12.0 + 0.5*t + 10.0*mouse.x);

  vec4 ro = vec4(0.0, 0., - 2.0, 0.);
  vec4 rd;

  float fov = 1.3;
  vec3 ta = vec3( 0.0, 0.0, 0.0 );
  vec3 ww = normalize( ta - ro.xyz );
  vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
  vec3 vv = normalize( cross(uu,ww));
  rd = normalize(vec4(uv.x*uu + uv.y*vv + fov*ww, 0.));

  float d = 0.0, dS = 999.0, minDS = 999.0;
  float ed = 999.0, ed2 = ed;
  float steps = 0.0;
  vec4 p = ro;
  for(int i = 0; i < 50; i ++ ) {
    steps ++ ;
    ed = 999.;
    for (float w = -1.41; w < 1.41; w += .05) {
      ed = min(ed, distFunc((vec4(p.xyz, p.w + w)) * boxTrans));
    }
    dS = ed;
    minDS = min(minDS, dS);
    p = p + dS * rd;
    d += dS;
    if (dS < 0.001 || d > 100.0)break;
  }

  vec3 RED = zoth.yxx * 0.3;

  float noise = mkNoise(uv * grow_shrink, t, minDS);
  col += noise;
  col += S(0.01, 0.0, dS) * WHITE * (.5 + .5 * rainbow(uv, u_time + 2.0));
  col += S(0.023, 0.005, minDS) * rainbow(uv, u_time + 2.0);

  gl_FragColor = vec4(col, 1.0);
}

