    vec2 uv = vec2(atan(p.x,p.z)/(3.14159*4.),p.y*.04);
    tree.x -= texture(iChannel3, uv).r*.5;
    res = colMin(res,tree);
    vec3 torP = p;
    torP-=vec3(0,0,-15);
    torP.yz*=Rot(1.5707);

    vec2 torRock = vec2(sdTorus(torP, vec2(5,.9)),7.);
    uv = vec2(atan(torP.x,p.y)/(3.14159/1.),torP.y*.4);
    torRock.x -= texture(iChannel1, uv).r*.5;
    res = colMin(res,torRock);

    torP = p;
    torP-=vec3(5,0,-15);
    torP.yz*=Rot(1.5707);
    torP.xy*=Rot(1.2);

    torRock = vec2(sdTorus(torP, vec2(5,.9)),7.);
    uv = vec2(atan(torP.x,p.y)/(3.14159/1.),torP.y*.4);
    torRock.x -= texture(iChannel1, uv).r*.5;
    res = colMin(res,torRock);


    vec2 vine = vec2(sdCylinder(p-vec3(sin(iTime*2.+p.y)*.1,0,-8), vec3(0,0,0), vec3(0,8,0), .1),5.);
    vec2 vine2 = vec2(sdCylinder(p-vec3(sin(iTime*2.+p.y)*.2+1.,0,sin(iTime*3.+p.y)*.2-8.), vec3(0,0,0), vec3(0,8,0), .1),5.);
    uv = vec2(atan(p.x+10.,p.z)/(3.14159*10.),p.y/10.);
    vine.x-=texture(iChannel1, uv).r*.5;
    uv = vec2(atan(p.x+10.,p.z)/(3.14159*15.),p.y/20.);
    vine2.x-=texture(iChannel1, uv).r*.4;
    //vec2 leaf = vec2(dBox(p-vec3(sin(iTime*2.+p.y)*.1,1.+abs((-p.z-7.9))*2.,-7.9), vec3(pow(-p.z-7.9,2.),.01,.4)),5);
    //p.y=mod(p.y, 2.0/(2.*sin(p.y))) + 1.0;
    //vec2 leaf =vec2(dBox(p-vec3(sin(iTime*2.+p.y)*.1, 1.0,-7.98+2.*sin(abs(p.x-sin(iTime*2.)*.1))),.1*vec3(sin(p.y),2.-.1*.3*sin(4.+(p.x-sin(iTime*2.)*.1)*3.),.1)),5);
    //vine = smin(vine, leaf,.2);
    res = colMin(res,vine);
    res = colMin(res,vine2);
  //  res.x = leaf.x;
    return res;
}
vec2 RayMarch(vec3 ro, vec3 rd){
    // distance from origin
    vec2 dO=vec2(0.,0.);
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO.x*rd;

        // get distance to seam
        vec2 ds = GetDist(p);
        //move origin to new point
        dO+=ds.x*.7;
        if(ds.x < SURFACE_DIST){
            dO.y = ds.y;
            break;
        }
        else if( dO.x > MAX_DIST){
            dO.y= -1.;
            break;
        }
    }
    return dO;
}
vec3 GetNormal(vec3 p){
    //distance to point being analyzed
    float d = GetDist(p).x;

    //distance to another point along the objects surface that is closeby
    vec2 e = vec2(0.01,0);

    //slope between the two points
    //note: swizzel is the .xxy or .yyx etc
    vec3 n = d - vec3(
         GetDist(p-e.xyy).x,
         GetDist(p-e.yxy).x,
         GetDist(p-e.yyx).x);

    return normalize(n);

}
float GetLight(vec3 p){
    //position of the light source
    vec3 lightPos = vec3(4,5.,10);

    lightPos.xz += vec2(sin(iTime),cos(iTime));
    //light vector
    vec3 l = normalize(lightPos-p);

    //normal of object
    vec3 n = GetNormal(p);

    // dot product of the light vector and normal of the point
    // will give us the amount of lighting to apply to the point
    // dot() evaluates to values between -1 and 1, so we will clamp it
    float diff = clamp(dot(n, l),0.,1.);

    // calculate if point should be a shadow:
    // raymarch from point being calculated towards light source
    // if hits surface of something else before the light,
    // then it must be obstructed and thus is a shadow
    // the slight offset "p+n*SURFACE_DIST*1.1" is needed to ensure the
    // break condistions in the function are not met too early
    float d = RayMarch(p+n*SURFACE_DIST*1.1,l).x;
    if(d < length(lightPos-p)){
        diff *= .1;
    }

    return diff;
}
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}
float DrawSquare (vec2 uv, float width, float height, float posX, float posY, float blur){
    float w = length((uv.x + posX));
    float h = length(uv.y + posY);
    float col = smoothstep(width,width-blur, w);
    col *= smoothstep(height,height-blur, h);
    return col;
}
vec3 A (vec2 uv){
    vec3 base =vec3(1)*DrawSquare (uv-vec2(0,0), .1-uv.y*.2, .2, 0., 0., .01);
    vec3 mask =vec3(1)*DrawSquare (uv-vec2(0,.1), .05-uv.y*.2, .07, 0., 0., .01);
    vec3 mask2 =vec3(1)*DrawSquare (uv-vec2(0,-.1), .05-uv.y*.2, .1, 0., 0., .01);
    return base - mask - mask2;
}
vec3 X (vec2 uv){
    vec3 slashR = vec3(1)*DrawSquare (uv-vec2(uv.y*.5,0), .04, .2, 0., 0., .01);
    vec3 slashL = vec3(1)*DrawSquare (uv-vec2(-uv.y*.5,0), .04, .2, 0., 0., .01);
    return slashR+slashL;
}
vec3 coneText(vec2 uv){
    vec3 col = A(uv-vec2(-.3,0))+X(uv-vec2(0,0));
    return col;
}
vec3 Render (inout vec3 ro, inout vec3 rd, inout float reflVal){
//------------------
    vec2 d = RayMarch(ro,rd);
// position of point
    // start at ray origin
    // move d units in the ray direction
    vec3 p = ro + rd *d.x;
 //get diffuse lighting
    float diff = GetLight(p);
    vec3 col = vec3(0);
    col = vec3(diff);

    vec3 n = GetNormal(p);
    vec3 r = reflect(rd, n);
    vec3 refl = texture(iChannel0, r).rgb;
    refl= refl*abs(n.y) + refl*abs(n.z) + refl*abs(n.x);

    if(d.y == 1.){
        vec3 gCol = smoothstep(.1,.5,p.y)*vec3(0,1,0);
        vec3 bCol = smoothstep(.15,.1,p.y)*vec3(0,1,1)*.4;
        col *= gCol+bCol;

        //col*=refl;
        reflVal = .1;
    }
    else if(d.y==2.){
       col *= vec3(0,1,1);
       reflVal = 0.9;
    }
    else if(d.y==3.){
        float off = pow(sin(iTime*2.),2.)*.5+1.;
        vec2 uv = vec2(atan(p.x,p.z)/(3.14159),p.y-off);
        uv.x-=.4;
        uv.y-=.3;
        col*=vec3(1,0,0);
        col-= coneText(uv).rgb;
        reflVal = 0.1;
    }
    else if(d.y==4.){
      col*=vec3(1.,.7,.5)*.6;
      col+=vec3(0,1,0)*smoothstep(4.,0.,p.y)*.3;
     // vec2 uv = vec2(atan(p.x,p.z)/(3.14159*4.),p.y*.04);
      //col*=texture(iChannel3, uv).rgb;
      reflVal=0.6*smoothstep(4.,0.,p.y);
    }
    else if(d.y==5.){
      col*=.3*vec3(0,1,.5);
      vec2 uv = vec2(atan(p.x+10.,p.z)/(3.14159*4.),p.y*.04);
      col.b+=smoothstep(.5,1.,texture(iChannel3, uv).r);
      reflVal=0.6*smoothstep(4.,0.,p.y);
    }
    else if(d.y==6.){
      col=vec3(1.,1,0);
      col.xy*=sin(iTime*3.)*.5+.5;
      reflVal=0.9;
    }
    else if(d.y==7.){
      col*=.6*vec3(1.);
      col+=vec3(0,1,0)*smoothstep(4.,0.,p.y)*.3;
      reflVal=0.9*smoothstep(6.,0.,p.y);
    }
    else{
        col=texture(iChannel0, rd).rgb;
    }
     ro = p+n*SURFACE_DIST*3.;
     rd=r;
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    // origin is now (0,0)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 mo = iMouse.xy/iResolution.xy;



    //camera
    //-----------------
    // ray origin
    //controls position
    vec3 ta = vec3( 1., 5., -3);
    vec3 ro = ta + vec3( 0, .5,1.);
    mat3 ca = setCamera( ro, ta,0.);
//    vec3 ro = vec3(0,3,-5);


    //ray dir
    //controls rotation
    #if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 pt = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
#else    
        vec2 pt = (2.0*fragCoord-iResolution.xy)/iResolution.y;
#endif
    vec3 rd = ca * normalize( vec3(pt,2.5) );



    float reflVal = 1.;
    vec3 col = Render(ro,rd,reflVal);
    vec3 bounce = reflVal*Render(ro,rd,reflVal);
    col += bounce;
    /*vec3 bounce2 = reflVal*Render(ro,rd,reflVal);
    col += bounce2;*/
    fragColor = vec4(col,1.0);
}

