// Forked from https://www.shadertoy.com/view/tdS3DG
// Modelled to look like the North Star Combiner Optics

#define AA 2   // make this 3 is you have a fast computer

//------------------------------------------------------------------

// generic ellipsoid - improved approximated distance
float sdEllipsoid( in vec3 p, in vec3 r ) 
{
    float  k0 = length(p/r);
    float  k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

// prolate ellipsoid defined by two foci and the radius of the minor axis
float sdEllipsoidFoci( in vec3 p, in vec3 f1, in vec3 f2, in float minorAxis ) 
{
    // Calculate Major Axis
    float halfLength    = length(f2 - f1) * 0.5;
    float halfMinorAxis =       minorAxis * 0.5;
    float halfMajorAxis     = sqrt((halfLength * halfLength) + (halfMinorAxis * halfMinorAxis));
    vec3  r             = vec3(halfMajorAxis, halfMinorAxis, halfMinorAxis);

    // Calculate the input point in local space
    mat3 rotation;
    rotation[0] = normalize(f1 - f2); // Use Graham-Schmidt to hallucinate y, z bases
    rotation[1] = normalize(cross(vec3(0,0,1), rotation[0])); // Twist about x doesn't matter  
    rotation[2] = normalize(cross(rotation[0], rotation[1])); // because Minor Axes are symmetric
    rotation    = transpose(rotation); // Transpose because GLSL indexes matrices funny
    rotation    = inverse  (rotation); // Invert to reverse the rotation
    vec3 localp = (p - ((f1 + f2) * 0.5)) * rotation;

    return sdEllipsoid(localp , r);
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float opUnion       ( float d1, float d2 ) { return min( d1,d2); }
float opSubtraction ( float d1, float d2 ) { return max(-d1,d2); }
float opIntersection( float d1, float d2 ) { return max( d1,d2); }

float approxNorthStarCombiner( vec3 p )
{
    vec3 lf1 = vec3(-0.02626856, 0.007116468, -0.03609176);
    vec3 rf1 = vec3( 0.02626856, 0.007116468, -0.03609176);
    vec3 lf2 = vec3(-0.2060344, 0.05526515, -0.07436727);
    vec3 rf2 = vec3( 0.2060344, 0.05526515, -0.07436727);

    // Outer and Inner Ellipsoid Surfaces
    float d11 = sdEllipsoidFoci( p, lf1, lf2, 0.244949 ); // Inner Surfaces
    float d12 = sdEllipsoidFoci( p, rf1, rf2, 0.244949 );
    float d31 = d11 - 0.001; // Outer Surfaces
    float d32 = d12 - 0.001;
    float d1  = opUnion(d11, d12);
    float d3  = opUnion(d31, d32);

    // Subtract inner (reflecting) surface from outer surface
    d1 = opSubtraction (d1, d3); 

    // Cut out the outer edges of the ellipsoids
    d1 = opIntersection(d1, sdRoundBox( p + vec3(0.0, 0.015 , -0.11), vec3(0.08, 0.03, 0.075), 0.005 )); 

    // Cut out the nose sort of
    d1 = opSubtraction(     sdRoundBox( p + vec3(0.0, 0.2  ,-0.15), vec3(0.00, 0.2  , 2   ), 0.01 ), d1);

    // Debug Spheres for Foci
    //d1 = opUnion(d1, length(p - lf1) - 0.003 );
    //d1 = opUnion(d1, length(p - lf2) - 0.003 );
    //d1 = opUnion(d1, length(p - rf1) - 0.003 );
    //d1 = opUnion(d1, length(p - rf2) - 0.003 );

    return d1;
}

//------------------------------------------------------------------

vec2 map( in vec3 p, int id )
{
    // North Star Reflector
    float d1 = approxNorthStarCombiner(p*0.2); // *0.2 scales it up 5x so it's easier to see

    // Ground Plane
    float d2 = p.y+0.3;

    return (d1<d2) ? vec2(d1,1.0) : vec2(d2,2.0);
}

vec2 castRay( in vec3 ro, in vec3 rd, int id )
{
    float m = 0.0;
    float t = 0.0;
    const float tmax = 100.0;
    for( int i=0; i<200 && t<tmax; i++ )
    {
	    vec2 h = map( ro+rd*t, id );
        if( h.x<0.001 ) break;
        m = h.y;
        t += h.x;
    }

    return (t<tmax) ? vec2(t,m) : vec2(0.0);
}


float calcSoftshadow( in vec3 ro, in vec3 rd, in int id)
{
	float res = 1.0;
    float t = 0.01;
    for( int i=0; i<256; i++ )
    {
		float h = map( ro + rd*t, id ).x;
        res = min( res, smoothstep(0.0,1.0,8.0*h/t ));
        t += clamp( h, 0.005, 0.02 );
        if( res<0.001 || t>5.0 ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 calcNormal( in vec3 pos, in int id )
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy, id ).x + 
					  e.yyx*map( pos + e.yyx, id ).x + 
					  e.yxy*map( pos + e.yxy, id ).x + 
					  e.xxx*map( pos + e.xxx, id ).x );
}

float calcAO( in vec3 pos, in vec3 nor, in int id )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos, id).x;
        occ += (hr-dd)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );    
}

// https://iquilezles.org/articles/checkerfiltering
float checkersGradBox( in vec2 p )
{
    // filter kernel
    vec2 w = fwidth(p) + 0.001;
    // analytical integral (box filter)
    vec2 i = 2.0*(abs(fract((p-0.5*w)*0.5)-0.5)-abs(fract((p+0.5*w)*0.5)-0.5))/w;
    // xor pattern
    return 0.5 - 0.5*i.x*i.y;                  
}

vec3 render( in vec3 ro, in vec3 rd, int id )
{ 
    vec3 col = vec3(0.0);

    vec2  res = castRay(ro,rd, id);

    if( res.y>0.5 )
    {
        float t   = res.x;
        vec3  pos = ro + t*rd;
        vec3  nor;
        float occ;

        // material        
        if( res.y>1.5 )
        {
        	nor = vec3(0.0,1.0,0.0);
            col = 0.05*vec3(1.0);
            col *= 0.7+0.3*checkersGradBox( pos.xz*2.0 );
            occ = 1.0;

        }
        else
        {
            nor = calcNormal( pos, id );
            occ = 0.5+0.5*nor.y;
            col = vec3(0.2);
        }

        // lighting
        occ *= calcAO( pos, nor, id );

        vec3  lig = normalize( vec3(-0.5, 1.9, 0.8) );
        vec3  hal = normalize( lig-rd );
        float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);

        float sha = calcSoftshadow( pos, lig, id );
        sha = sha*sha;

        float spe = pow( clamp( dot( nor, hal ), 0.0, 1.0 ),32.0)*
                    dif * sha *
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,rd),0.0,1.0), 5.0 ));

        //vec3 lin = vec3(0.0);
        //lin += 2.00*dif*vec3(3.30,2.50,2.00)*sha;
        //lin += 0.50*amb*vec3(0.30,0.60,1.50)*occ;
        //lin += 0.30*bac*vec3(0.40,0.30,0.25)*occ;
        //col = col*lin;
        //col += 2.00*spe*vec3(3.30,2.50,2.00);

        col *= 5.0;
        col *= vec3(0.2,0.3,0.4)*amb*occ + 1.6*vec3(1.0,0.9,0.75)*dif*sha;
        col += vec3(2.8,2.2,1.8)*spe*3.0;            



        //col = mix( col, vec3(0.1), 1.0-exp(-0.03*t) );
    }
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera	
    vec3 ro = vec3( 1.0*cos(0.8*iTime), 0.12, 1.0*sin(0.8*iTime) );
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    // camera-to-world transformation
    vec3 cw = normalize(ta-ro);
    vec3 cu = normalize( cross(cw,vec3(0.0, 1.0,0.0)) );
    vec3 cv =          ( cross(cu,cw) );

    // scene selection
    int id = 0;

    // render
    vec3 tot = vec3(0.0);
	#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 fc = o + vec2( fragCoord.x, fragCoord.y);
		#else    
        vec2 fc = vec2( fragCoord.x, fragCoord.y);
		#endif
        vec2 p = (-vec2(iResolution.x,iResolution.y) + 2.0*fc)/iResolution.y;

        // ray direction
        vec3 rd = normalize( p.x*cu + p.y*cv + 2.0*cw );

        // render	
        vec3 col = render( ro, rd, id );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
	#if AA>1
    }
    tot /= float(AA*AA);
	#endif

    fragColor = vec4( tot, 1.0 );
}

