vec2 GetDist(vec3 p){
    float off =sin(5.*iTime+p.x)*.1+sin(3.*iTime+p.z)*.1;
    off*=sin(iTime+p.x*2.);
    off+=sin(7.*iTime+(length(p)-.4)*7.*smoothstep(7.,1.,length(p)-.4))*.01;
    vec2 res = vec2(p.y+off,1.);
    off = pow(sin(iTime*2.),2.)*.5+1.;
    vec2 base = vec2(sdCapsule(p-vec3(0,-smoothstep(off-.5,off,p.y),0), vec3(0.), vec3(0,off,0), .8),2.);


    res = colMin(res, base);
    vec2 cone = vec2(sdCapsule(p-vec3(0,1.3-smoothstep(off,off+.8,p.y),0), vec3(0,-1,0), vec3(0,off,0), .5-(p.y-off)*.2),3.);
    res = colMin(res, cone);

    vec2 washer = vec2(sdTorus(p-vec3(0,off-.2,0), vec2(.5,.1)),4.);
    res = colMin(res, washer);

    vec2 screw = vec2(sdCylinder(p-vec3(.55,off-.3,0), vec3(0,.3,0), vec3(0,0,0), .05-pow(sin((off-p.y)*100.),2.)*.02),4.);

    res = colMin(res, screw);
    vec2 screw2 = vec2(sdCylinder(p-vec3(.23,off-.3,.5), vec3(0,.3,0), vec3(0,0,0), .05-pow(sin((off-p.y)*100.),2.)*.02),4.);

    res = colMin(res, screw2);
    p.xz *=Rot(iTime);
    vec2 lightBase = vec2(dBox(p-vec3(0,off+.8,0), vec3(.2,.2,.2)),5.);
    vec2 lightBaseM = vec2(dBox(p-vec3(0,off+.8,0), vec3(.5,.1,.1)),5.);
    vec2 lightBaseM2 = vec2(dBox(p-vec3(0,off+.8,0), vec3(.1,.1,.5)),5.);
    lightBase.x = min(lightBase.x,lightBaseM.x);
    lightBase.x = min(lightBase.x,lightBaseM2.x);

    res = colMin(res, lightBase);

    vec2 light = vec2(sdCapsule(p-vec3(0,off+1.,0), vec3(0,0,0), vec3(0,.3,0), .05+(p.y-off)*.2),6.);
    res = colMin(res, light);
    //res = light;
    return res;
}
vec2 RayMarch(vec3 ro, vec3 rd){
    // distance from origin
    vec2 dO=vec2(0.,0.);
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO.x*rd;

        // get distance to seam
        vec2 ds = GetDist(p);
        //move origin to new point
        dO+=ds.x*.4;
        if(ds.x < SURFACE_DIST){
            dO.y = ds.y;
            break;
        }
        else if( dO.x > MAX_DIST){
            dO.y= -1.;
            break;
        }
    }
    return dO;
}
vec3 GetNormal(vec3 p){
    //distance to point being analyzed
    float d = GetDist(p).x;

    //distance to another point along the objects surface that is closeby
    vec2 e = vec2(0.01,0);

    //slope between the two points
    //note: swizzel is the .xxy or .yyx etc
    vec3 n = d - vec3(
         GetDist(p-e.xyy).x,
         GetDist(p-e.yxy).x,
         GetDist(p-e.yyx).x);

    return normalize(n);

}
float GetLight(vec3 p){
    //position of the light source
    vec3 lightPos = vec3(4,5.,10);

    lightPos.xz += vec2(sin(iTime),cos(iTime));
    //light vector
    vec3 l = normalize(lightPos-p);

    //normal of object
    vec3 n = GetNormal(p);

    // dot product of the light vector and normal of the point
    // will give us the amount of lighting to apply to the point
    // dot() evaluates to values between -1 and 1, so we will clamp it
    float diff = clamp(dot(n, l),0.,1.);

    // calculate if point should be a shadow:
    // raymarch from point being calculated towards light source
    // if hits surface of something else before the light,
    // then it must be obstructed and thus is a shadow
    // the slight offset "p+n*SURFACE_DIST*1.1" is needed to ensure the
    // break condistions in the function are not met too early
    float d = RayMarch(p+n*SURFACE_DIST*1.1,l).x;
    if(d < length(lightPos-p)){
        diff *= .1;
    }

    return diff;
}
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}
float DrawSquare (vec2 uv, float width, float height, float posX, float posY, float blur){
    float w = length((uv.x + posX));
    float h = length(uv.y + posY);
    float col = smoothstep(width,width-blur, w);
    col *= smoothstep(height,height-blur, h);
    return col;
}
vec3 A (vec2 uv){
    vec3 base =vec3(1)*DrawSquare (uv-vec2(0,0), .1-uv.y*.2, .2, 0., 0., .01);
    vec3 mask =vec3(1)*DrawSquare (uv-vec2(0,.1), .05-uv.y*.2, .07, 0., 0., .01);
    vec3 mask2 =vec3(1)*DrawSquare (uv-vec2(0,-.1), .05-uv.y*.2, .1, 0., 0., .01);
    return base - mask - mask2;
}
vec3 X (vec2 uv){
    vec3 slashR = vec3(1)*DrawSquare (uv-vec2(uv.y*.5,0), .04, .2, 0., 0., .01);
    vec3 slashL = vec3(1)*DrawSquare (uv-vec2(-uv.y*.5,0), .04, .2, 0., 0., .01);
    return slashR+slashL;
}
vec3 coneText(vec2 uv){
    vec3 col = A(uv-vec2(-.3,0))+X(uv-vec2(0,0));
    return col;
}
vec3 Render (inout vec3 ro, inout vec3 rd, inout float reflVal){
//------------------
    vec2 d = RayMarch(ro,rd);
// position of point
    // start at ray origin
    // move d units in the ray direction
    vec3 p = ro + rd *d.x;
 //get diffuse lighting
    float diff = GetLight(p);
    vec3 col = vec3(0);
    col = vec3(diff);

    vec3 n = GetNormal(p);
    vec3 r = reflect(rd, n);
    vec3 refl = texture(iChannel0, r).rgb;
    refl= refl*abs(n.y) + refl*abs(n.z) + refl*abs(n.x);

    if(d.y == 1.){
        col *= vec3(0,.7,1);
        col*=refl;
        reflVal = .9;
    }
    else if(d.y==2.){
       col *= vec3(1,0,0);
       reflVal = 0.1;
    }
    else if(d.y==3.){
        float off = pow(sin(iTime*2.),2.)*.5+1.;
        vec2 uv = vec2(atan(p.x,p.z)/(3.14159),p.y-off);
        uv.x-=.4;
        uv.y-=.3;
        col*=vec3(1,0,0);
        col-= coneText(uv).rgb;
        reflVal = 0.1;
    }
    else if(d.y==4.){
      col*=vec3(1.);
      reflVal=0.9;
    }
    else if(d.y==5.){
      col*=vec3(1.);
      reflVal=0.9;
    }
    else if(d.y==6.){
      col=vec3(1.,1,0);
      col.xy*=sin(iTime*3.)*.5+.5;
      reflVal=0.9;
    }
    else{
        col=texture(iChannel0, rd).rgb*.3;
    }
     ro = p+n*SURFACE_DIST*3.;
     rd=r;
    return col;
}
