vec3 rainbow(vec2 uv, float time) {
  return sin(vec3(time) + uv.xyx * vec3(1.0, 1.0, 1.0) + vec3(0, 2, 4)) * 0.49 + 0.5;
}


float sdBoxEdge(in vec3 p, in vec3 b) {
  return length(best2(abs(p) - b));
}

// purely a guess based on the one for a cube
float sdTesseractEdge(in vec4 p, in vec4 b) {
  vec4 d = abs(p) - b;
  //d.w /= 5.;
  vec3 db = best3(d);
  return length(db);
}

vec4 getIDGV(vec2 uv, float scale) {
  vec2 id = floor(uv * scale);
  vec2 gv = fract(uv * scale) - 0.5;
  return vec4(id, gv);
}

float mkNoise(vec2 uv, float t, float minDS) {
  float scale = fwidth(uv.x) * 100.0 * BG_SCALE;
  vec4 idgv = getIDGV(uv, scale);
  vec2 id = idgv.xy;
  vec2 gv = idgv.zw;
  float n = N21b(id);
  float transFreq = BASE_TWINKLE * n + 0.01;
  t -= 3877.627 * n;
  float transProgress = fract(t * transFreq);
  float transId = floor(t * transFreq);
  float lastIdOffset = N21b(vec2(n, transId - 1.0));
  float idOffset = N21b(vec2(n, transId));
  float nextIdOffset = N21b(vec2(n, transId + 1.0));
  float noiseThresh = STAR_RARITY;
  float noise = S(1.0, 0.0, transProgress) * S(noiseThresh, 1.0, N21b(id + lastIdOffset));
  noise += S(noiseThresh, 1.0, N21b(id + idOffset));
  noise += S(0.0, 1.0, transProgress) * S(noiseThresh, 1.0, N21b(id + nextIdOffset));
  return S(0.0, 0.2, minOf(0.5 - abs(gv))) * (noise * S(0.1, 2.0, minDS));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (gl_FragCoord.xy - .5 * u_resolution.xy) / u_resolution.y;
  vec2 mouse = (u_mouse.xy - 0.5 * u_resolution.xy) / u_resolution.y;
  float t = u_time / 9.0;
  vec3 col = BLACK;
  uv *= 4.0;

  float fw = max(fwidth(uv.x), fwidth(uv.y));

  float grow_shrink = GROW_SHRINK * sin(t * PI) * 0.1 + 0.9;

  vec4 s = vec4(1.0, 1.0, 1.0, 1.0) * grow_shrink;

  vec4 p = vec4(uv, - 4.0, N21b(uv) * 4. - 2.);

  mat4 boxTrans = rot4(mouse.x * TAU / 2.0 + .347856, 1) * rot4(mouse.y * TAU / 2.0 + .37465, 4);
  boxTrans *= rot4(t * 13.0 / 17.0 * ROTATE_IDLE + PI / 3.0, 1) * rot4(t * 13.0 / 11.0 * ROTATE_IDLE - 17.0 * PI / 7.0, 2);
  boxTrans *= rot4(t * 11.0 / 17.0 * ROTATE_IDLE + PI / 3.0, 2) * rot4(t * 13.0 / 11.0 * ROTATE_IDLE - 17.0 * PI / 7.0, 3);
  boxTrans *= rot4(t * 12.0 / 17.0 * ROTATE_IDLE + PI / 3.0, 4);
  boxTrans *= rot4(t * 13.0 / 17.0 * ROTATE_IDLE, 5);

  // raymarch over the orthogonal scene
  vec2 sc = sinCos(vec2(t));
  vec4 rd = normalize(vec4(0.0, 0.0, 1.0, 0.0));
  float d = 0.0, dS = 999.0, minDS = 999.0, ed;
  float steps = 0.0;
  for(int i = 0; i < 50; i ++ ) {
    ed = 999.;
    steps ++ ;
    float w = 0.0;
    for (float w = -1.3; w < 1.3; w += .1) {
        //if (w < minOf(s * boxTrans)) continue;
        vec4 p_ = vec4(p.xyz, p.w + w) * boxTrans;
        ed = min(ed, sdTesseractEdge(p_, s) - 0.01);
    }

    dS = ed;
    // dS = min(edgeDist, sdBox(p*boxTrans, s));
    minDS = min(minDS, dS);
    p = p + dS * rd * .5;
    d += dS;
    if (dS < 0.0001 || d > 100.0)break;
  }


  float noise = mkNoise(uv * grow_shrink, t, 1.);
  col += noise;
  col += S(0.01, 0.0, dS) * WHITE;
  col += S(0.023, 0.005, minDS) * rainbow(uv, u_time + 2.0);
  //col = minDS == 0. ? WHITE : RED;

  vec4 idgv = getIDGV(uv, 1.0);
  vec2 id = idgv.xy, gv = idgv.zw;


  gl_FragColor = vec4(col, 1.0);
}

#define u_resolution iResolution
#define u_mouse iMouse
#define u_time iTime
#define gl_FragColor fragColor

#define PI 3.141592653589
#define TAU 6.28318530718

#define S(a, b, t)smoothstep(a, b, t)

#define BLACK vec3(0.0)
#define WHITE vec3(1.0)

#define GROW_SHRINK 0.3
#define ROTATE_IDLE 0.2
#define BASE_TWINKLE 99.0
#define STAR_RARITY 0.95
// set to 5k+ for a hyperspace bungee; curious noise patterns at 2000.
#define BG_SCALE 100.0

/* lib/util stuff */

float N21b(vec2 p) {  // from an art of code video i think
  vec3 a = fract(vec3(p.xyx) * vec3(213.897, 653.453, 253.098));
  a += dot(a, a.yzx + 79.76);
  return fract((a.x + a.y) * a.z);
}


float sdBox(in vec3 p, in vec3 b) {
  vec3 d = abs(p) - b;
  return length(max(d, 0.0)) + min(max(d.z, max(d.x, d.y)), 0.0);
}


vec2 best2(vec3 p) {
  vec2 r = p.x > p.y ? vec2(p.x, p.y) : vec2(p.y, p.x);
  if (p.z > r.x) { return vec2(p.z, r.x);}
  if (p.z > r.y) { return vec2(r.x, p.z);}
  return r;
}

float minOf(vec2 v) {
  return min(v.x, v.y);
}

vec2 sinCos(in vec2 ab) {
  return vec2(sin(ab.x), cos(ab.y));
}

// roll from XY to XZ or YZ (0 no roll, 1=XZ, 2=YZ)
mat3 rot3XY(float angle, int roll) {
  float s = sin(angle), c = cos(angle);
  mat3 r = mat3(c, - s, 0.0, s, c, 0.0, 0.0, 0.0, 1.0);
  if (roll == 1) {
    r = mat3(r[0].xzy, r[2].xzy, r[1].xzy);
  } else if (roll == 2) {
    r = mat3(r[2].zxy, r[0].zxy, r[1].zxy);
  }
  return r;
}


vec3 rainbow(vec2 uv, float time) {
  return sin(vec3(time) + uv.xyx * vec3(1.0, 1.0, 1.0) + vec3(0, 2, 4)) * 0.49 + 0.5;
}

/* meat */

float sdBoxEdge(in vec3 p, in vec3 b) {
  return length(best2(abs(p) - b));  // rly elegant
}

vec4 getIDGV(vec2 uv, float scale) {
  vec2 id = floor(uv * scale);
  vec2 gv = fract(uv * scale) - 0.5;
  return vec4(id, gv);
}

float mkNoise(vec2 uv, float t, float minDS) {
  float scale = fwidth(uv.x) * 100.0 * BG_SCALE;
  vec4 idgv = getIDGV(uv, scale);
  vec2 id = idgv.xy;
  vec2 gv = idgv.zw;
  float n = N21b(id);
  float transFreq = BASE_TWINKLE * n + 0.01;
  t -= 3877.627 * n;
  float transProgress = fract(t * transFreq);
  float transId = floor(t * transFreq);
  float lastIdOffset = N21b(vec2(n, transId - 1.0));
  float idOffset = N21b(vec2(n, transId));
  float nextIdOffset = N21b(vec2(n, transId + 1.0));
  float noiseThresh = STAR_RARITY;
  float noise = S(1.0, 0.0, transProgress) * S(noiseThresh, 1.0, N21b(id + lastIdOffset));
  noise += S(noiseThresh, 1.0, N21b(id + idOffset));
  noise += S(0.0, 1.0, transProgress) * S(noiseThresh, 1.0, N21b(id + nextIdOffset));
  return S(0.0, 0.2, minOf(0.5 - abs(gv))) * (noise * S(0.1, 2.0, minDS));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (gl_FragCoord.xy - .5 * u_resolution.xy) / u_resolution.y;
  vec2 mouse = (u_mouse.xy - 0.5 * u_resolution.xy) / u_resolution.y;
  float t = u_time / 9.0;
  vec3 col = BLACK;
  uv *= 4.0;

  float fw = max(fwidth(uv.x), fwidth(uv.y));

  float grow_shrink = GROW_SHRINK * sin(t * PI) * 0.1 + 0.9;

  vec3 s = vec3(1.0, 1.0, 1.0) * grow_shrink;
  vec3 p = vec3(uv, - 4.0);

  mat3 boxTrans = rot3XY(mouse.x * TAU / 2.0, 1) * rot3XY(mouse.y * TAU / 2.0, 2);
  boxTrans *= rot3XY(t * 13.0 / 17.0 * ROTATE_IDLE + PI / 3.0, 0) * rot3XY(t * 13.0 / 11.0 * ROTATE_IDLE - 17.0 * PI / 7.0, 2);

  // raymarch over the orthogonal scene
  vec2 sc = sinCos(vec2(t));
  vec3 rd = normalize(vec3(0.0, 0.0, 1.0));
  float d = 0.0, dS = 999.0, minDS = 999.0, edgeDist;
  float steps = 0.0;
  for(int i = 0; i < 1000; i ++ ) {
    steps ++ ;
    edgeDist = sdBoxEdge(p * boxTrans, s) - 0.01;
    dS = edgeDist;
    //dS = min(edgeDist, sdBox(p*boxTrans, s));
    minDS = min(minDS, dS);
    p = p + dS * rd;
    d += dS;
    if (dS < 0.0001 || d > 100.0)break;
  }

  float noise = mkNoise(uv * grow_shrink, t, minDS);
  col += noise;
  col += S(0.01, 0.0, minDS) * WHITE;
  col += S(0.023, 0.005, minDS) * rainbow(uv, u_time + 2.0);

  gl_FragColor = vec4(col, 1.0);
}

