void mainImage(out vec4 fragColor, in vec2 fragCoord){
  float col2 = 0.;
  float col = 0.;
  for (float i = -.5; i < .5; i += .25) {
    for (float j = -.5; j < .5; j += .25) {
      col = 0.;
      vec2 uv = (fragCoord + vec2(i, j) - iResolution.xy * .5) / (iResolution.y * .5);
      float c = uv.y < 0. ? 1. : 0.;
      float s1 = 3.1;
      float f1 = .645;
      float s2 = 1.55;
      float f2 = 1./3.;
      if (iTime < 1.) {
        float d1 = dot(uv, uv);
        vec2 z2 = (uv + vec2(f1, 0.)) * s1;
        vec2 z3 = (uv - vec2(f2, 0.)) * s2;
        float d3 = dot(z3, z3);
        col = b2(d3) * b(d1) * c * b(dot(z2, z2)) + (1. - b2(d3)) * b(d3);
        col += b2(d1);
        vec2 z4 = (z3 + vec2(f1, 0.)) * s1;
        vec2 z5 = (z3 - vec2(f2, 0.)) * s2;
        float d4 = dot(z4, z4);
        col = b2(d4) * col * b(dot(z5, z5)) + (1. - b2(d4)) * b(d4) * c;
      }else{
        vec2 z2 = (uv + vec2(f1, 0.)) * s1;
        vec2 z3 = (uv - vec2(f2, 0.)) * s2;
        vec2 z4 = (z3 + vec2(f1, 0.)) * s1;
        vec2 z5 = (z3 - vec2(f2, 0.)) * s2;
        col = f(uv);
        if (dot(z2, z2) < .95) {
          col = f(z2);
        }else{
          if (dot(z4, z4) < .95) {
            col = f(z4);
          }else{
            if (dot(z5, z5) < .95) {
              col = f(z5);
            }
          }
        }
      }
      col2 += clamp(col, 0., 1.);
    }
  }
  col2 /= 16.;
  fragColor = vec4(col2, col2, col2, 1.);
}

