float hash21(vec2 p){ return fract(sin(dot(p,vec2(23.86,48.32)))*4374.432); }
float lsp(float begin, float end, float t) { return clamp((t - begin) / (end - begin), 0.0, 1.0); }

mat2 rot(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a)); }

//@iq - all sdf's
float box(vec3 p, vec3 s) 
{
    p = abs(p)-s;
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}
float box(vec2 p,vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}
float cap( vec3 p, float h, float r )
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float vcap( vec3 p, float h, float r )
{
    p.y -= clamp( p.y, 0.0, h );
    return length( p ) - r;
}

const float drep = 12.;
const float angle = 2.*PI/drep;
const float angfl = angle/2.;
const float hfrep = drep/2.;

float polar(inout vec2 p) 
{
    //float angle = 2.*PI/rep;
    float a = atan(p.y, p.x) + angfl;
    float c = floor(a/angle);
    a = mod(a,angle) - angfl;
    p = vec2(cos(a), sin(a))*length(p);
    return (abs(c) >= (hfrep)) ? abs(c) : c;
} 

//constants
const float sz = 6.;
const float hf = sz/2.;
const float db = sz*2.;

//global
float ftime=0.,ga1,ga2,ga3,ga4,tmod=0.,speed=0.;
mat2 turn,rurn,r45,rx,ry;
vec3 hit=vec3(0),hitPoint=vec3(0),gid=vec3(0),sid=vec3(0);

float column(vec3 q)
{
    float clm = cap(q,.3,1.5);
    vec3 p =vec3(q.x,abs(q.y),q.z);
    float pcap = box(p-vec3(0,1.45,0),vec3(.5,.1,.5))-.005;
          pcap = min(cap(p-vec3(0,1.3,0),.4,.05),pcap);

    polar(q.xz);
    float grv = vcap(q-vec3(.3,-1.1,0),2.2,.045);
    return min(pcap,max(clm,-grv));
}

vec2 map(vec3 p)
{
    p.x-=T*.4;
    vec2 res = vec2(1e5,0.);
    vec3 q = p;

    vec3 qid = floor((q+hf)/sz)+vec3(11);
    vec3 ofs = q+vec3(hf);
    vec3 xid = floor((ofs+hf)/sz);
    vec3 tt = q;

    float fhs;
    if(tmod<4.){
      	fhs = hash21(qid.zy);
        tt.x += (fhs>.5) ? ftime : -ftime;
    } else {
      	fhs = hash21(qid.xy);
        tt.z += (fhs>.5) ? ftime : -ftime;
    }

    q = mod(q+hf,sz)-hf;
    vec3 tid = floor((tt+hf)/sz);
    tt = mod(tt+hf,sz)-hf;

    if(tmod<4.){
        tt.xy*=(fhs>.5) ? turn : rurn;
    }else{
        tt.yz*=(fhs>.5) ? rurn : turn;
    }

    float sphere = length(abs(q)-hf)-1.15;
    sphere = min(length( vec2(abs(q.z)-hf,abs(q.y)-hf))-.1,sphere);
    if(p.y>-1.65) sphere = 0.5;
    if(sphere<res.x)
    {
        res = vec2(sphere,2.);
    	hit=q;
        gid=xid+25.;
    }

    float pillar = column( vec3(abs(q.x)-hf,q.y,abs(q.z)-hf));
    pillar=min(column( vec3(abs(q.x)-hf,q.z,abs(q.y)-hf)),pillar);
    if(p.y>-1.65) pillar = 0.5;
    if(pillar<res.x)
    {
        res = vec2(pillar,4.);
    	hit=q;
        gid=qid+25.;
    }

    float balls = length(abs(tt)-.45)-.45;
    balls=max(box(tt,vec3(.55)),balls);
    if(p.y>1.) balls = 1.;
    if(balls<res.x)
    {
        res = vec2(balls,1.);
    	hit=q;
        gid=tid+25.;
    }

    return res;
}

vec3 normal(vec3 p, float t)
{
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

vec3 hue(float t)
{ 
    vec3 c = vec3(0.325,0.976,0.867);
    return .50 + .35*cos(T*.3+PI2*t*(c*vec3(.98,.99,.97))); 
}

void topLayer(inout vec3 C, vec2 uv)
{
    float px = fwidth(uv.x);
    uv*=rot(.03);
    float bx = box(uv-vec2(-.02,.02),vec2(.85,.45))-.0125;
    float dx = abs(bx)-.0025;
    dx=smoothstep(px,-px,dx);
    bx=smoothstep(-px,px,bx);

    float bd = box(uv-vec2(.065,-.055),vec2(.825,.425))-.0125;
    float sd =smoothstep(.085-px,px,bd);

    float ln = texture(iChannel0,uv*.75).r;
    ln=smoothstep(.42-px,px,ln);
    vec3 base = vec3(.83);
    base = mix(base*.3,base,clamp((uv.y+.5),0.,1.));
    vec3 acct = hue(55.)*.1;
    vec3 edge = mix(vec3(.95),acct,clamp((uv.y+.5),0.,1.));
    C=mix(C,base,bx);
    C=mix(C,acct,min(ln,bx));
    C=mix(C,C*.15,min(sd,bx));
    C=mix(C,edge,dx);
    C=mix(C,C*.8,max(hash21(uv),bx));

}
void mainImage( out vec4 O, in vec2 F )
{   
    // precal    
    float zoom = mod(T*.1,4.)<2. ? 10. : 6.;
    tmod=mod(T,8.);
    ftime = fract(T*.25)*sz;
    r45=rot(-0.78539816339);
    turn=rot(ftime*PI/2.);
    rurn=rot(-ftime*PI/2.);
    //

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(uv*zoom,-zoom);
    vec3 rd = vec3(0.,0.,1.);

    ro.yz*=r45;
    ro.xz*=r45;
    rd.yz*=r45;
    rd.xz*=r45;

    vec3 FC = mix(hue(3.),hue(95.),clamp((uv.y+.5)*.75,0.,1.));
    vec3 C = vec3(0);
    vec3 p;
    float d=0.,m=0.;

    if(uv.y>-.48&&uv.y<.52 && uv.x>-.9&&uv.x<.87)
    {      
        //marcher
        for(int i=0;i<100;i++)
        {
            p=ro+rd*d;
            vec2 ray = map(p);
            d += ray.x;
            m  = ray.y;
            if(abs(ray.x)<MIN_DIST ||d>MAX_DIST)break;
        }

        float alpha = 1.;
        sid=gid;

        if(d<MAX_DIST)
        {
            alpha = 0.;
            vec3 n = normal(p,d);
            vec3 lpos =vec3(3, 8., -7.);
            vec3 l = normalize(lpos-p);
            float diff =  clamp(dot(n,l),0.,1.);

            float shdw = 1.;
            for( float t=.03; t < 13.; )
            {
                float h = map(p + l*t).x;
                if( h<MIN_DIST ) { shdw = 0.; break; }
                shdw = min(shdw, 24.*h/t);
                t += h * .25;
                if( shdw<MIN_DIST || t>25. ) break;
            }

            vec3 h = vec3(.125);

            if(m==1.) {
                vec3 view = normalize(p - ro);
                vec3 ret = reflect(normalize(lpos), n);
                float spec = .75 * pow(max(dot(view, ret), 0.), 10.);
                h=hue(14.732*(sid.y+5.));
                diff = mix(diff,diff*shdw,.65);
                C = vec3(diff*h)+spec;
            }

            if(m==2.) {
                diff=d*.1;
                h=hue(33.6+(sid.z*.05));
                diff = mix(diff,diff*shdw,.65);
                C = diff*h;
            }

            if(m==4.){
                vec3 view = normalize(p - ro);
                vec3 ret = reflect(normalize(lpos), n);
                float spec = .75 * pow(max(dot(view, ret), 0.), 10.);
                h = hue(41.5);
                diff = mix(diff,diff*shdw,.65);
                C = vec3(diff*h)+spec;
            }

        } 

        C = mix(FC,C,exp(-.000095*d*d*d));
    }

    topLayer(C,uv);
    C=pow(C, vec3(.4545));
    C=clamp(C,vec3(.03),vec3(1.));

    O = vec4(C,1.0);
}

// See https://www.desmos.com/calculator/7w04yjoiwx
// See also @iq's shader about x^y=y^x https://www.shadertoy.com/view/XdySR3
// W1(x) and W2(x) are the two branches of the Lambert W function (https://en.wikipedia.org/wiki/Lambert_W_function)
// According to the wikipedia article, they are actually denotated W_0(x) and W_-1(x)
// but that convention doesn't work in GLSL syntax

#define draw(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))

float h1(in float x) { return 0.62 * log(x + 0.5) + 0.3; }
float h2(in float x) { return -4.68 - 10.0 * x; }
float n(in float y, in float x) { return y - (y - x / exp(y)) / (y + 1.0); } // Newton-Raphson
float W1(in float x) { return n(n(n(n(n(n(n(n(h1(x), x), x), x), x), x), x), x), x); } // y > -1
float W2(in float x) { return n(n(n(n(n(n(n(n(h2(x), x), x), x), x), x), x), x), x); } // y < -1

// Solutions (two possible)
float f(in float x) { return exp(-W1(-log(x) / x)); }
float g(in float x) { return exp(-W2(-log(x) / x)); }

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y * 8.0;
    float unit = 16.0 / iResolution.y;
    vec3 color = vec3(1.0);

    // Grid
    draw(abs(fract(uv.x + 0.5) - 0.5) + 0.25 * unit, vec3(0.0, 0.0, 1.0));
    draw(abs(fract(uv.y + 0.5) - 0.5) + 0.25 * unit, vec3(0.0, 0.0, 1.0));
    draw(abs(uv.x), vec3(1.0, 0.0, 0.0));
    draw(abs(uv.y), vec3(1.0, 0.0, 0.0));

    // Draw the solved equation (two parts)
    if (mod(iTime, 4.0) < 2.0) {
        float y = f(uv.x);
        float dx = (f(uv.x + 0.001) - y) / 0.001;
        draw(abs(uv.y - y) / sqrt(1.0 + dx * dx) - 0.01, vec3(1.0, 0.0, 0.0));

        y = g(uv.x);
        dx = (g(uv.x + 0.001) - y) / 0.001;
        draw(abs(uv.y - y) / sqrt(1.0 + dx * dx) - 0.01, vec3(0.0, 0.0, 1.0));
    }

    // Draw the implicit equation
    else {
        #define f(x, y) (pow(x, y) - pow(y, x))
        float d = f(uv.x, uv.y);
        vec2 g = (vec2(f(uv.x + 0.001, uv.y), f(uv.x, uv.y + 0.001)) - d) / 0.001;
        draw(abs(d) / length(g), vec3(0.0));
    }

    fragColor = vec4(color, 1.0);
}

