    )
    {
        p=g*d;
        p+=vec3(.3,.3,-1.8);
        p=R(p,vec3(.577),iTime*.1);
        s=3.;
        for(int j=0;j++<8;)
            p=clamp(p,-.5,.5)*2.-p,
            s*=e=7.*clamp(.3/min(dot(p,p),1.),.0,1.),
