#define zoomMagnitude 0.0
#define zoomRate      0.5

//DAVE HOSKINS' HASH FUNCTIONS
//https://www.shadertoy.com/view/4djSRW

vec2 rnd23(vec3 p3){
    p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 off = rnd23(vec3(fragCoord, iTime)) - .5;
    vec2 uv = (fragCoord + off - iResolution.xy * .5) / iResolution.y;
    int i = 0;
    vec2 z = uv * ((cos(iTime * zoomRate) + 1.) * zoomMagnitude + 0.09);
    z -= vec2(.57, .58);
    vec2 c = z;
    for (i = 0; i < 1024; i++) {
        z = vec2(z.x * z.x - z.y * z.y + c.x, 2. * z.x * z.y + c.y);
        if (dot(z, z) > 4.) {
            break;
        }
    }
    float j = float(i);
    float j2 = j - log2(log2(dot(z, z))) + 4.;
    vec3 dat = vec3(j / 1024., j2 / 1024., 0.);
    fragColor = vec4(dat, 1.0);
}

