#define lightStrength 30.
#define ambientLight  0.2

#define pi  3.1415926535897932
#define pi2 1.57079632679

float tex(vec2 p){
    return pow(texture(iChannel0, p / iResolution.xy).g, .1) * 20.;;
}

float tex2(vec2 p){
    return pow(1. - texture(iChannel0, p / iResolution.xy).g, 2.);
}

float sm(float x, float a){ //Thingy I solved for integrating the lambert cosine thingy over a solid angle with angle measure a, since the sun has volume in the sky
    x = acos(x);
    return (sin(max(-pi2, min(x + a * .5, pi2))) - sin(max(-pi2, min(x - a * .5, pi2)))) / a;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 z = fragCoord;
    vec2 muv = 3. * (iMouse.xy - iResolution.xy * 0.5) / iResolution.y;
    vec3 n = vec3(tex(z) - tex(z - vec2(5, 0)), tex(z) - tex(z - vec2(0, 5)), 0.025);
    n = normalize(n);
    vec2 z2 = (z - iResolution.xy * 0.5) / (.5 * iResolution.y);
    float lambert = dot(n, normalize(vec3(muv.x - z2.x, muv.y - z2.y, 1.)));
    vec2 off = muv - z2;
    lambert = max(sm(lambert, 0.1), 0.1) / (dot(off, off) * 2. + .1 + tex(z) * 5. - 60.);
    vec3 col = lambert * vec3(1., 0.5, 0.65) * lightStrength + ambientLight * vec3(0.1, 0.5, 1.) * pow(tex2(z), 5.) * (max(dot(n, vec3(0., 0., 1.) * 2.), 0.) + .1);
    fragColor = vec4(col, 1.);
}

