#define sampleReprojectionCompensation 0.0

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord / iResolution.xy;
    float k = texture(iChannel0, uv).r*2.;
    vec3 k2 = texture(iChannel1, uv).rgb;
    k2 = max(k2, 1e-6);
    float j = round(k * 512.);
    float o1 = (fract(j/3.) + 0.25) / (cos(500. * k)*5. + 5.);
    float o2 = (fract(j/3.) + 0.25) / (cos(80.  * k)*5. + 5.);
    float o3 = (fract(j/3.) + 0.25) / (cos(50.  * k)*5. + 5.);
    vec3 col = vec3(o1, o2, o3);
    col = (col*27. + (o1 + o2 + o3)) / 30.;
    col = 1. - 1. / (1. + k2 * k2 * max(min(col, 1.), 0.));
    col = clamp(col, 0., 1.);
    col *= 1.5;
    float zch = -sin(iTime*.5)*sampleReprojectionCompensation; //Derivative of zoom function lets us predict how to reproject samples
    uv -= 0.5;
    uv *= 1. + zch;
    uv += 0.5;
    fragColor = vec4((texture(iChannel2, uv).rgb*15. + col)/16., 0.);
}

