#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,a=normalize(vec3(1,2,3)),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=.8;
        ++i<100.;
        O.xyz+=mix(vec3(1),H(cos(log(s)))*.5,.8)*.05*exp(-.5*e*i*i)
    )
    {
        p=g*d;
        p.z+=iTime*.5;
        s=4.;
        vec4 q=vec4(p,.05);
        q.xyw=R(q.xyw,a,2.+iTime*.05);
        q.xzw=R(q.xzw,a,.5+cos(iTime*.05));
        q.zyw=R(q.zyw,a,1.+sin(iTime*.05));

        for(int i;i++<8;)
            q=mod(q-1.,2.)-1.,
            s*=e=1.2/dot(q,q)-.04,
            q*=e;
        g+=e=length(q-clamp(q,-.5,.5))/s+5e-4;
    }
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=.7;
        ++i<160.;
        O.xyz+=mix(vec3(1),H(cos(log(s)))*.5,.8)*.01*exp(-80.*e*i*i)
    )
    {
        p=g*d;
        p.z-=2.;
        p=R(p,normalize(vec3(1,2,3)),iTime*.2);
        s=3.;
        vec4 q=vec4(p,tanh(sin(iTime*3.))*.2);

        for(int i;i++<8;)
            q=mod(q-1.,2.)-1.,
            s*=e=1.4/dot(q,q)-.03,
            q*=e;
        g+=e=length(q-clamp(q,-.7,.7))/s;
    }
}

