#define deg2rad (3.141/360.)
#define MAX_STEPS 1024
#define MAX_DIST 1e5
#define SURF_DIST 1e-4

float roundedCubeSDF(vec3 p, vec3 s, float r) {
    vec3 q = abs(p) - s;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0)-r;
}

float roundedCylinderSDF( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

vec2 characterSDF(vec3 p){
    int mat;
    p.y -= 3.9;
	float head = roundedCylinderSDF(p, 0.3, 0.3, .3);//Head
	float body = roundedCubeSDF(p-vec3(0,-1.6,0), vec3(1,1,.5), .05);//Body

	float l_arm = roundedCubeSDF(p-vec3(1.6,-1.6,0), vec3(0.5,1,.5), .05);//left arm
	float r_arm = roundedCubeSDF(p-vec3(-1.6,-1.6,0), vec3(0.5,1,.5), .05);//right arm
    float arm = min(l_arm, r_arm);

    float l_leg = roundedCubeSDF(p-vec3(0.51,-3.7,0), vec3(0.46,1,.5), .05);//left leg
	float r_leg = roundedCubeSDF(p-vec3(-0.51,-3.7,0), vec3(0.46,1,.5), .05);//right leg
    float leg = min(l_leg, r_leg);

    body = min(body, arm);
    body = min(body, leg);

    float d = min(head, body);

    if(d == head){
        mat = 1;
    }if(d == body){
        mat = 2;
    }if(body == arm){
        mat = 1;
    }if( d == leg){
        mat = 3;
    }


	return vec2(d, mat);
}

vec2 scene(vec3 p){
    int mat;
    float ground = p.y+1.0;
	float d = ground;
    vec2 character = characterSDF(p);
    d = min(d, character.x);
    if(d == character.x){
        mat = int(character.y);
    }if(d == ground){
        mat = 4;
    }

	return vec2(d, mat);
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(SURF_DIST, 0.0);

    return normalize(
        scene(p).x - vec3(
            scene(p - e.xyy).x,
            scene(p - e.yxy).x,
            scene(p - e.yyx).x
        )
    );
}

float AO(vec3 p) {
	float occ = 0.0;
    float sca = 1.0;
    vec3 nor = GetNormal(p);
    for(int i = 0; i < 5; i++) {
        float h = 0.001 + 0.15*float(i)/4.0;
        float d = scene(p + h*nor).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 1.5*occ, 0.0, 1.0 );
}

float RayMarch(vec3 ro, vec3 rd) {
    float dO = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        float dS = scene(ro + rd * dO).x;
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;
}

mat3 rot(vec3 ang) {
    vec3 s = sin(ang);
    vec3 c = cos(ang);
    mat3 x = mat3(
        vec3(1, 0, 0),
        vec3(0, c.x,-s.x),
        vec3(0, s.x, c.x)
    );
    mat3 y = mat3(
        vec3(c.y, 0, s.y),
        vec3(0, 1, 0),
        vec3(-s.y, 0, c.y)
    );
    mat3 z = mat3(
        vec3(c.z, s.z, 0),
        vec3(-s.z, c.z, 0),
        vec3(0, 0, 1)
    );
    return x*y*z;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/max(iResolution.x, iResolution.y)*2.;
	vec3 ro = vec3(0, 2, -6);
    vec3 rd = normalize(vec3(uv, 75.*deg2rad));

    vec2 t = (iMouse.xy-0.5*iResolution.xy)/max(iResolution.x, iResolution.y);
    t *= 4.0;
    vec2 mouse_rotation = t*2.;
    if(length(iMouse.xy) < 1e-3) mouse_rotation = vec2(0);

    vec2 yawpitch = vec2(mouse_rotation.x, -mouse_rotation.y);
    mat3 viewmat = rot(vec3(yawpitch.y, yawpitch.x, 0));

    ro *= viewmat;
    rd *= viewmat;

    vec3 skin = vec3(0.945,0.914,0.686);
    vec3 tshirt = vec3(0.753,0.000,0.000);
    vec3 pant = vec3(0.149,0.149,0.149);

    float d = RayMarch(ro, rd);
    vec3 p = ro + rd * d;
    vec3 col = vec3(0);

    if(d < MAX_DIST){
        int mat = int(scene(p).y);
    	vec3 lp = normalize(vec3(0,10,-3));
    	vec3 n = GetNormal(p);
		col = vec3(smoothstep(-0.2,1.,dot(n, lp)));
        float s = RayMarch(p+n*SURF_DIST*2.0, lp);
        col *= AO(p); 
		if(s < 5.) col *= vec3(.5);
        if(mat == 1){
            col *= skin;
        }if(mat == 2){
            col *= tshirt;
        }if(mat == 3){
            col *= pant;
        }if(mat == 4){
            col *= vec3(0.580,1.000,0.608);;
        }
        col += vec3(0.110,0.596,0.898)*.1;
    }else{
        col = vec3(0.110,0.596,0.898);
    }


    fragColor = vec4(col, 1.0);
}

