#define OFFSET 10.0

vec4 gammaRamp(vec4 c) {
    c.x=pow(c.x,2.2);
    c.y=pow(c.y,2.2);
    c.z=pow(c.z,2.2);
    return c;
}

// copy from https://www.shadertoy.com/view/4l2GzW
float r(float n)
{
 	return fract(cos(n*89.42)*343.42);
}
vec2 r(vec2 n)
{
 	return vec2(r(n.x*23.62-300.0+n.y*34.35),r(n.x*45.13+256.0+n.y*38.89)); 
}
float worley(vec2 n,float s)
{
    float dis = 2.0;
    for(int x = -1;x<=1;x++)
    {
        for(int y = -1;y<=1;y++)
        {
            vec2 p = floor(n/s)+vec2(x,y);
            float d = length(r(p)+vec2(x,y)-fract(n/s));
            if (dis>d)
            {
             	dis = d;   
            }
        }
    }
    return 1.0 - dis;

}

// copy from https://www.shadertoy.com/view/4sc3z2

#define MOD3 vec3(.1031,.11369,.13787)

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}
float perlin_noise(vec3 p)
{
    p += OFFSET;
    vec3 pi = floor(p);
    vec3 pf = p - pi;

    vec3 w = pf * pf * (3.0 - 2.0 * pf);

    return 	mix(
        		mix(
                	mix(dot(pf - vec3(0, 0, 0), hash33(pi + vec3(0, 0, 0))), 
                        dot(pf - vec3(1, 0, 0), hash33(pi + vec3(1, 0, 0))),
                       	w.x),
                	mix(dot(pf - vec3(0, 0, 1), hash33(pi + vec3(0, 0, 1))), 
                        dot(pf - vec3(1, 0, 1), hash33(pi + vec3(1, 0, 1))),
                       	w.x),
                	w.z),
        		mix(
                    mix(dot(pf - vec3(0, 1, 0), hash33(pi + vec3(0, 1, 0))), 
                        dot(pf - vec3(1, 1, 0), hash33(pi + vec3(1, 1, 0))),
                       	w.x),
                   	mix(dot(pf - vec3(0, 1, 1), hash33(pi + vec3(0, 1, 1))), 
                        dot(pf - vec3(1, 1, 1), hash33(pi + vec3(1, 1, 1))),
                       	w.x),
                	w.z),
    			w.y);
}

float mixNoise (vec3 uv) {
    float dis = (1.0+perlin_noise(vec3(uv.x, uv.y + perlin_noise(vec3(uv.x*10.0, 0, 0))*2.0,  uv.z)*8.0)) 
        * (1.0+(worley(uv.xy, 32.0)+
        0.5*worley(2.0*uv.xy,32.0) +
        0.25*worley(4.0*uv.xy,32.0) ));

        return dis/4.0;
}

float mag (vec3 v) {
    return sqrt((v*v).x + (v*v).y +(v*v).z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/iResolution.xy;
    float dis = mixNoise(vec3(vec2(p.x*100.0, p.y) ,0));

    float light = mixNoise(vec3(0, (p.x + p.y)*0.5 + iTime, 0))* 2.3;
    if(!LIGHTING) light = 1.0;
    vec3 col = vec3(dis*BRUSH+(1.0-BRUSH)) * COL;
    float smudges = perlin_noise(vec3(p*20.0 + 
                    perlin_noise(vec3(p*30.0 +
                    perlin_noise(vec3(p*40.0 + 
                    perlin_noise(vec3(p*50.0, 0)) , 0)), 0)) , 0))* SMUDGES;
    fragColor = gammaRamp(vec4(mix(col, vec3(1.0 - mag(col) * light), smudges) * light,1.0));

}

#define STEPS 164.0
#define MDIST 200.0
#define time iTime
#define pmod(p,x) (mod(p,x)-(x)*0.5)
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pi 3.1415926535
float g1 = 0.0;
float anim(){
  return fract(time*0.15);
}
float smin(float a, float b, float k){
  float h = max(0.0,k-abs(a-b));
  return min(a,b)-h*h*0.25/k;
}

float smax(float a, float b, float k){
  float h = max(0.0,k-abs(a-b));
  return max(a,b)-h*h*0.25/k;
}

float gy(vec3 p){
  return dot(sin(p.xyz),cos(p.yzx));
}
float box( vec3 p, vec3 b ){
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
vec2 map(vec3 p){
  vec3 po = p;
  float streamfft = 0.02;
  float t = mod(time,300.0);
  vec2 a = vec2(0);
  vec2 b = vec2(1);
  //bottom plane gyroid

  b.x = gy(p*2.0+(t*2.0+5.0*texelFetch( iChannel0, ivec2(64,0), 0 ).x*vec3(1,-1,0)));
  b.x*=0.55;
  //bottom plane
  a.x = p.y+1.0-sin(p.x*2.5+t)*0.15-sin(p.z*2.5+t)*0.05;

  a.x = max(b.x,a.x);

  float voff = texelFetch( iChannel0, ivec2(16.0,0), 0 ).x*streamfft*1.5;
  //voff=clamp(voff,0.0,12.0/800.0);
  //ball gyriod
  float ballsize = 2.5;
  if(anim()>0.5){
    //p.xz = pmod(p.xz,30.0);
    ballsize = 3.0+voff*500.0;

  }
  ballsize = clamp(ballsize,1.5,15.0);
  b.x = abs(abs(gy(-p*3.0+t+8.0*streamfft*texelFetch( iChannel0, ivec2(64,0), 0 ).x))-0.6)-0.2;
  b.x*=0.4;
  //ball

  //p.y = pmod(p.y+sin(t)*3.0,10.0);
  p.xz*=rot(t);
  //p.yz*=rot(t);
  float ballcut = box(p-vec3(0,1.0+voff*125.0,0),vec3(ballsize));
  p = po;
  ballcut = mix(ballcut, length(p-vec3(0,voff*125.0,0))-ballsize,sin(time*2.0)*0.5+0.5);
  b.x = smax(b.x,ballcut,0.2);
  p = po;

  a = (a.x<b.x)?a:b;
  t*=0.5;
  float tt = pow(fract(t),3.0)+floor(t);

  tt*=pi/2.0;

  b.y = 2.0;
  p.xy*=rot(tt);

  float moddist = 20.0+voff*500.0;
  p.xz = pmod(p.xz,moddist);
  b.x = length(p.xz)-1.5-clamp(sin(p.y*1.5),0.0,0.2);
  a = (a.x<b.x)?a:b;
  p = po;

  p.xy*=rot(tt);
  p.xy = pmod(p.xy,moddist);
  b.x = length(p.xy)-1.5-clamp(sin(p.z*1.5),0.0,0.2);
  a = (a.x<b.x)?a:b;

  p = po;

  p.xy*=rot(tt);
  p.yz = pmod(p.yz,moddist);
  b.x = length(p.yz)-1.5-clamp(sin(p.x*1.5),0.0,0.2);
  a = (a.x<b.x)?a:b;
  p = po;
  t = iTime;

  p-=vec3(0,t*5.0,0);
  p=pmod(p,23.0);

  b.x = length(p)-2.0;
  g1+=0.01/(0.01+b.x*b.x);
  a = (a.x<b.x)?a:b;


  return a;
}
vec3 norm(vec3 p){
  vec2 e = vec2(0.01,0.0);
  return normalize(map(p).x - vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}
