// Scrapyard by Kristian Sivonen (ruojake)
// CC BY-SA 4.0 (https://creativecommons.org/licenses/by-sa/4.0/)

// set to 1 if you dare - slows down to crawl on my GPU
#define AA 0

#define sat(x) clamp((x), 1., 0.)

float hash(vec3 p)
{
    p = fract(p * 21.43125);
    p += dot(p, p.zxy * 321.32136);
    return fract(((p.x + p.z) * p.y)*10.);
}

mat2 rot(float a)
{
    float s = sin(a),
          c = cos(a);
    return mat2(c, -s, s, c);
}

// smooth min(q<0)/max(q>=0)
float sm(float a, float b, float q)
{
    float d = clamp((b - a) / q + .5, 0., 1.);
    return mix(a,b,d) + .5 * q * d * (1. - d);
}

float elem(vec3 i, vec3 f, vec3 c)
{
    float h = hash(i+c) * .5;
    vec3 p = f - c;
    vec3 d = abs(p) - h;
    return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, vec3(0.)));
}


float grid(vec3 p)
{
    vec3 i = floor(p);
    vec3 f = p - i;
    const vec2 o = vec2(1,0);

    return min(
        min(
            min(elem(i, f, o.yyy), elem(i, f, o.yyx)),
            min(elem(i, f, o.yxy), elem(i, f, o.yxx))
        )
        ,
        min(
            min(elem(i, f, o.xyy), elem(i, f, o.xyx)),
            min(elem(i, f, o.xxy), elem(i, f, o.xxx))
        )
    );
}

vec2 scene(vec3 p, float th)
{
    float res = p.y;
    if(p.y > .3) return vec2(res, 0);
    if(p.y <= .015) return vec2(p.y - .015, 0);
    float s = 1.;
    const mat3 r = mat3(-.66, 1.51, -1.12,
                        1.07, 1.28, 1.1,
                        1.55, -.23, -1.23);
    const float q = .4;

    for(int i = 0; i < 8; i++)
    {
        float d = sm(s * grid(p), res - s*.1, q*s*s);
        res = mix(sm(d, res, -q*s*s), res, clamp(th/s * 2. - 1.,0.,1.));

        p *= r;
        s *= .5;

        if(th > s) break;
    }
    return vec2(res, 1);
}

vec3 normal(vec3 p, float d, float th)
{
    vec2 e = vec2(.1 * th, 0);
    return normalize(d - vec3(
        scene(p - e.xyy, th).x,
        scene(p - e.yxy, th).x,
        scene(p - e.yyx, th).x));
}

vec3 sky(vec3 rd, vec3 ld, float k, float l)
{
    vec3 col = mix(vec3(.05, .2, .5), vec3(1., .97, .96), min(rd.y * .5 + .5, l));
    float sun = sat(dot(rd, ld));
    k = max(k, .0001);
    col += l * pow(sat((sun - .9992 + k) / k), 5. / k) * (30. - 25. * k * (2. - k)) + sun * sun * vec3(1.,.9,.8) * (.25 + k);
    return col;
}

// https://google.github.io/filament/Filament.md.html#table_texturedfg
vec3 DFGAppr(vec3 f0, float ndv, float roughness)
{
    vec4 c0 = vec4(-1., -.0275, -.572, .022);
    vec4 c1 = vec4(1., .0425, 1.04, -.04);
    vec4 r = roughness * c0 + c1;
    float a004 = min(r.x * r.x, exp2(-9.28 * ndv)) * r.x + r.y;
    vec2 ab = vec2(-1.04, 1.04) * a004 + r.zw;
    return f0 * ab.x + ab.y;
}

float shadow(vec3 ro, vec3 rd, float maxDist, float k)
{
    float res = 1.;
    float d = 0.;
    float t = .01;
    for(int i = 0; i < 30; ++i)
    {
        d = scene(ro + rd * t, .01).x * 2.;
        res = min(res, k * d / t);
        t += d;
    	if(abs(d) < .0001 || t >= maxDist)
            break;
        if (res < .001)
        {
        	res = 0.;
            break;
        }
    }
    return res;
}

vec3 brdf(vec3 albedo, float metal, float roughness, vec3 rd, vec3 ld, vec3 n, float l)
{
    vec3 spec = sky(reflect(rd, n), ld, max(roughness, .0001), l);

    vec3 diff = albedo * sky(n, ld, 1., 1.) * metal;
    vec3 f0 = max(albedo * (1. - metal), vec3(.04));
    return mix(diff, spec, DFGAppr(f0, dot(-rd, n), roughness));
}

vec3 reinhard(vec3 c)
{
    float l0 = dot(c, vec3(.2126, .7152, .0722));
    float l1 = l0 * (1. + l0 / 20.) / (1. + l0);
    return c / l0 * l1;
}

void img( out vec4 fragColor, in vec2 uv )
{  
    vec3 rd = normalize(vec3(uv, 1.5));
    mat2 camRot = rot(.3);
    rd.yz *= camRot;
    vec3 ro = vec3(uv * .02, 0);
    ro.yz *= camRot;
    ro += vec3(0,.5,iTime * .05);
    vec3 p = ro;
    float t = .01;
    vec2 d = vec2(0);
    float duv = length(fwidth(uv));
    float th = 0.;
    float i = 0.;

    for(;i < 1. && t < 25.; i += 1./128.)
    {
        p = ro + rd * t;
        th = clamp(duv * 3. * t - duv, 0., 1.);
        d = scene(p, th);
        t += d.x + (.00025 * t) * sign(d.x);
        if (abs(d.x)<.0005*t) break;
    }

    float ao = clamp(1.2 - i, 0., 1.);

    vec3 nml;
    if (d.y == 0.)
        nml = vec3(0,1,0);
    else if (t < 25.)
        nml = normal(p, d.x, th);
    else 
        nml = vec3(0, -1, 0);

    vec3 lDir = normalize(vec3(2,1,-1));
    float l = (dot(nml, lDir) * .5 + .5) * ao;

    vec3 shDir = d.y > 0. ? lDir : reflect(rd, nml);
    l *= shadow(p + nml * .01, shDir, 1., 4.5 - d.y * 2.); 

    vec3 col = vec3(0);
    if (d.y == 1.)
    {
        col = brdf(vec3(.1), .99, .3, rd, lDir, nml, l) 
            * (.7 + .3 * l);
    }
    else if (rd.y <= 0.)
    {
        col = brdf(vec3(.3, .025, .005), .8, .001, rd, lDir, nml, min(l, 1. + rd.y));
    }
    else
    {
        col = sky(rd, lDir, .01, 1.);
    }

    if (rd.y <= 0.)
        col = mix(col, sky(rd, lDir, 1., 1.), clamp(t * .05 - .4, 0., 1.));

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5)/iResolution.y;
    img(fragColor, uv);

    #if AA
    if (fwidth(fragColor.g) > .1)
    {
        fragColor *= 2.;
        vec2 offs = vec2(.7 * abs(dFdy(uv.y)),0);
        vec4 c;
        mat2 r = mat2(-.5,-.866,.866,-.5);
        for(int i = 0; i < 3; ++i)
        {
            img(c, uv + offs);
            offs *= r;
            fragColor += c; 
        }
        fragColor *= .2;
        //fragColor.rgb *= vec3(1,0,0);
    }
    #endif

    fragColor.rgb = reinhard(fragColor.rgb);

    fragColor.rgb = pow(fragColor.rgb, vec3(1./2.2));

    fragColor.rgb += hash(vec3(fragCoord, iFrame)) * .01 - .005;
}

// 'Mandellake' by Krischan
//
// 2D conversion of a *very* old article in the magazine
// Happy Computer, Edition 6/1987 by Frank GÃ¶ldner
// showing the mandelbrot fractal as a lake!
//
// Originally sources were available for the systems
// C64, Schneider CPC, Atari ST and Amiga :)
//

#define roughness 0.066
#define iterations 400
#define waterlevel 1.2
#define shorelevel 1.0
#define offset vec2(-1.0,0.0)
#define zoom 1.25

#define WATERCOLOR vec4(0.0,0.5,0.8,1.0)
#define SHORECOLOR vec4(0.3,0.5,0.3,1.0)
#define GRASSCOLOR vec4(0.2,0.5,0.1,1.0)

float mandellake(vec2 uv)
{

    float z=2.0;
    float xc=uv.x;
    float yc=uv.y;
    float xa=0.0;
    float ya=0.0;

    while(z>0.0)
    {
        float x2=abs(xa);
        float y2=abs(ya);
        float xn=x2-y2-xc;
        ya=xa*ya;
        ya=ya+ya-yc;
        xa=xn;
        z=z-roughness;
        if(z<0.0){break;}
        if(x2+y2>float(iterations)){break;}
    }

    return z;    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.0 - iResolution.xy) * 2.0/ iResolution.x;

    uv.x-=offset.x;
    uv.y+=offset.y;
    uv*=zoom;

    float r = mandellake(uv);

    fragColor=WATERCOLOR*vec4(1.0,r,1.0,1.0);

    if(r>shorelevel){fragColor = SHORECOLOR*vec4(vec3(r),1.0);}
    if(r>waterlevel){fragColor = GRASSCOLOR*vec4(vec3(r),1.0);}
}

