// Using Euler's reflection formula to extend the approximation
// here: https://www.shadertoy.com/view/stl3DS
// to negative values. The advantage is that there aren't any loops
// (at least explicit ones, there may be some iterative scheme in
// the sqrt(), pow(), and sinh() functions).

#define draw(d, c) color = mix(color, c, smoothstep(unit, 0.0, d))

const float e = 2.7182818284;
const float pi = 3.14159265359;
const float tau = 6.28318530718;

float pfactorial(in float x) {
    x += 1.0;
    float xx = x * x;
    return sqrt(tau / x) * pow(x / e * sqrt(x * sinh(1.0 / x) + 1.0 / (810.0 * xx * xx * xx)), x);
}

float factorial(in float x) {
    float y = pfactorial(abs(x));
    return x < 0.0 ? pi * x / (y * sin(pi * x)) : y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 8.0;
    float unit = 16.0 / iResolution.y;
    vec3 color = vec3(1.0);

    // Grid
    draw(abs(fract(uv.x + 0.5) - 0.5) + 0.25 * unit, vec3(0.0, 0.0, 1.0));
    draw(abs(fract(uv.y + 0.5) - 0.5) + 0.25 * unit, vec3(0.0, 0.0, 1.0));
    draw(abs(uv.x), vec3(1.0, 0.0, 0.0));
    draw(abs(uv.y), vec3(1.0, 0.0, 0.0));

    // Draw the function
    vec2 y = vec2(factorial(uv.x), 0.0);
    y.y = (factorial(uv.x + 0.001) - y.x) / 0.001;
    draw(abs(uv.y - y.x) / sqrt(1.0 + y.y * y.y) - 0.01, vec3(0.6, 0.0, 0.8));

    // Draw continuous Pascal's triangle
    //float n = uv.x + 8.0;
    //float k = uv.y + 4.0;
    //float binCoeff = round(gamma(n) / (gamma(k) * gamma(n - k))); // Rounding is affordable since I'm only wanting integers
    //color = vec3(binCoeff * 0.01);

    fragColor = vec4(color, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    fragColor = vec4(0.0);

    float y = pow(uv.x, 1.0 / 3.0);
    float dx = pow(uv.x, -2.0 / 3.0) / 3.0;
    vec4 color = vec4(1.0, 0.0, 0.0, 1.0);

    if (mod(iTime, 2.0) > 1.0) {
        y = sign(uv.x) * pow(abs(uv.x), 1.0 / 3.0);
        color = vec4(0.0, 1.0, 0.0, 1.0);
    }

    fragColor = mix(fragColor, color, smoothstep(unit, 0.0, abs(uv.y - y) / sqrt(1.0 + dx * dx)));
}

// Fork of "Peacock feather spiral 2" by jarble. https://shadertoy.com/view/7tjSRz
// 2021-07-22 21:19:20

#define ITERS 12

float getS0(vec2 uv, float t)
{
    float ret = 2.1;

    ret += (sin(t * 10.0 * (1.0 + sin(length(uv) * 0.3) * 0.3)) * 0.5 + 0.5) * sin(uv.x * 0.05) * 0.5;

    return ret;
}

float getS1(vec2 uv, float t)
{
    float ret = 1.125;

    ret *= 1.0 + sin(t * 35.0) * 0.001 * sin(length(uv) * 20.0);

    return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    float t=0.0;
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/15.0;
    uv.y += (iTime)/25.0;
    for(int c=0;c<ITERS;c++){
        float scale0 = getS0(uv, iTime / 25.0);
        float scale1 = getS1(uv, iTime / 25.0);
        float s1 = scale1*scale0;
        col_prev = col.yzx;
        for(int i=0;i<ITERS;i++)
        {

            uv= fract(-uv-((vec2(uv.x/scale0-uv.y/scale1,uv.y/scale0-uv.x/scale1)/(scale0))))/scale1;

            uv = fract(-uv.yx/s1)*s1;
            uv.x *= scale1;
            uv.y /= -scale1;
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

