-----------------------------------------------------------------------------
- How the data is stored:  --------------------------------------------------
the data is stored in a texture as an octree

the tree is stored as:
Node:
    8 pixels for each child

    and for each pixel (rgba, 4 uint8):
        if pixel.a = 0:
            it is a child !
            the index of the child is:
                idx = pixel.r + (pixel.g << 8) + (pixel.b << 16)

         if pixel.a = 1:
             it is an empty leaf

         if pixel.a = 2:
             it is a leaf
             the leaf color is pixel.rgb


if you want to see the octree, you can uncomment showTree on line 81

*/

//#define showTree

#define maxDepth 12

vec3 treeUp = vec3(2.0);
vec3 treeDown = vec3(-2.0);

vec3 col = vec3(0.0);
#define OUT -200.0

uvec4 treeTex(uint idx){
    uint w = uint(iChannelResolution[0].x);
    vec4 data = texelFetch(iChannel0, ivec2(idx % w, idx / w), 0); 
    return uvec4(data * 255.0);
}

float rand(vec3 p){
    uint idx = uint(p.x) + uint(p.y)*64u + uint(p.z)*64u*64u;
    idx ^= 3646844786u;
    idx *= 474746u;
    idx >>= 16;
    idx *= 563563u;
    idx >> 13;
    idx *= 454749u;
    return float(idx)/4294967295.0;
}

//tree traversal stuff

uint a;
uint find_firstNode(vec3 t0, vec3 tM){
    uint first = 0u;
    if(t0.x > t0.y && t0.x > t0.z){ // entry YZ
        if(tM.y < t0.x) first |= 2u;
        if(tM.z < t0.x) first |= 1u;
    }else if(t0.y > t0.z){ // entry XZ
        if(tM.x < t0.y) first |= 4u;
        if(tM.z < t0.y) first |= 1u;
    }else{  // entry XY
        if(tM.x < t0.z) first |= 4u;
        if(tM.y < t0.z) first |= 2u; 
    }
    return first;
}

uint next_Node(vec3 t1, uint yz, uint xz,uint xy){
    if(t1.x < t1.y && t1.x < t1.z){
        return yz;
    }else if(t1.y < t1.z){
        return xz;
    }else{
        return xy;
    }
}

vec4 procTree( vec3 t0, vec3 t1, out vec3 normal)
{   
    //init variables
    vec3 color;
    int actualDepth = 0;
    uint nodeIdx[maxDepth];
    uint childIdx[maxDepth];
    vec3 t0s[maxDepth];
    vec3 t1s[maxDepth];
    t0s[actualDepth] = t0;
    t1s[actualDepth] = t1;
    const int END = 8;
    const uvec3[] nextNode = uvec3[](uvec3( 4 , 2 , 1 ),
                                     uvec3( 5 , 3 ,END),
                                     uvec3( 6 ,END, 3 ),
                                     uvec3( 7 ,END,END),
                                     uvec3(END, 6 , 5 ),
                                     uvec3(END, 7 ,END),
                                     uvec3(END,END, 7 ),
                                     uvec3(END,END,END));

    vec3 tM = 0.5 * (t0 + t1);
    childIdx[actualDepth] = find_firstNode(t0, tM);
    nodeIdx[actualDepth] = 0u;
    uvec4 dataComp = treeTex(nodeIdx[actualDepth] + a ^ childIdx[actualDepth]);
    nodeIdx[actualDepth + 1] = dataComp.x + (dataComp.y << 8) + (dataComp.z << 16);

    // start loop
    for(int i = 0; i < 600; i ++){
        if (actualDepth < 0)return vec4(OUT);//visited all nodes

        uint currNode =  childIdx[actualDepth];
        dataComp = treeTex(nodeIdx[actualDepth] + a ^ childIdx[actualDepth]);
        t1 = t1s[actualDepth];
        if(t1.x <= 0.0 || t1.y <= 0.0 || t1.z <= 0.0) dataComp = uvec4(0, 0, 0, 1); 
        bool leaf = (dataComp.w != 0u);
        bool back = (currNode == 8u);

        if (leaf || back){
            if(back){actualDepth -= 1; currNode = childIdx[actualDepth];}
            t0 = t0s[actualDepth];
            t1 = t1s[actualDepth];
            tM = 0.5 * (t0 + t1);
            vec3 dt = t1 - t0;
            vec3 idx = vec3(float(currNode&4u)/4.0, float(currNode&2u)/2.0, float(currNode&1u));
            vec3 t0_ = t0 + 0.5*dt*idx;
            vec3 t1_ = t0 + 0.5*dt*(1.0 + idx);
            vec3 tM_ = 0.5*(t0_ + t1_);

            if (leaf && !back && (dataComp.w == 2u)){//the node is a full leaf
                t0 = t0_;
                color = vec3(dataComp.xyz)/255.0;

                if(t0.x > t0.y && t0.x > t0.z){ // entry YZ
                    normal = vec3(1.0, 0.0, 0.0);
                    return vec4(color, t0.x);
                }
                if(t0.y > t0.z){ // entry XZ
                    normal = vec3(0.0, 1.0, 0.0);
                    return vec4(color, t0.y);
                }
                // entry XY
                normal = vec3(0.0, 0.0, 1.0);
                return vec4(color, t0.z);


            }
            #ifdef showTree   
                col -= 0.011;
            #endif

            uvec3 next = nextNode[currNode];

            childIdx[actualDepth] = next_Node(t1_, next.x, next.y, next.z);
            dataComp = treeTex(nodeIdx[actualDepth] + a ^ childIdx[actualDepth]);
            nodeIdx[actualDepth + 1] = dataComp.x + (dataComp.y << 8) + (dataComp.z << 16);

        } else {
            t0 = t0s[actualDepth];
            t1 = t1s[actualDepth];
            actualDepth += 1;

            tM = 0.5 * (t0 + t1);

            vec3 dt = t1 - t0;
            vec3 idx = vec3(float(currNode&4u)/4.0, float(currNode&2u)/2.0, float(currNode&1u));
            vec3 t0_ = t0 + 0.5*dt*idx;
            vec3 t1_ = t0 + 0.5*dt*(1.0 + idx);
            vec3 tM_ = 0.5*(t0_ + t1_);
            childIdx[actualDepth] = find_firstNode(t0_, tM_);

            dataComp = treeTex(nodeIdx[actualDepth] + a ^ childIdx[actualDepth]);
            nodeIdx[actualDepth + 1] = dataComp.x + (dataComp.y << 8) + (dataComp.z << 16);

            t0s[actualDepth] = t0_;
            t1s[actualDepth] = t1_;
        }

    }
    return vec4(OUT);
}



vec4 ray_step(vec3 ro, vec3 rd, out vec3 normal){
    a = 0u;
    vec3 flip = -sign(rd);
    vec3 size = (treeUp + treeDown)*0.5;

    if (rd.x < 0.0){
        ro.x = size.x - ro.x;
        rd.x = -rd.x;
        a |= 4u;
    }
    if (rd.y < 0.0){
        ro.y = size.y - ro.y;
        rd.y = -rd.y;
        a |= 2u;
    }
    if (rd.z < 0.0){
        ro.z = size.z - ro.z;
        rd.z = -(rd.z);
        a |= 1u;
    }

    vec3 invDir = 1.0/rd;
    vec3 t0 = (treeDown - ro) * invDir;
    vec3 t1 = (treeUp - ro) * invDir;

    float tmin = max(max(t0.x, t0.y), t0.z);
    float tmax = min(min(t1.x, t1.y), t1.z);
    if ((tmin < tmax) && (tmax > 0.0f)){
        vec4 res = procTree(t0, t1, normal);
        normal *= flip;
        return res;
    }else{ //doesn't intersect
        return vec4(OUT);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    //test if any model is loaded
    if(iChannelResolution[0].x == iResolution.x &&
       iChannelResolution[0].y == iResolution.y){
        fragColor = texture(iChannel0, fragCoord/iResolution.xy);
        return;
    }
    vec2 uv = fragCoord/iResolution.xy*2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 0.3; // small FOV

    vec3 camPos = vec3(-3.0*cos(iTime*0.5), 0.1 + 0.6*sin(iTime*0.7*0.5), -3.0*sin(iTime*0.5))*1.9;
    vec3 target = vec3(0.0);

    vec3 dir0 = normalize(target - camPos);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 right = normalize(cross(up, dir0));
         up = cross(dir0, right);

    vec3 rd = normalize(right * uv.x + up * uv.y + dir0);

    //back ground
    vec3 col2 = vec3(0.082,0.086,0.137);

    //intersect object
    vec3 normal;
    vec4 res = ray_step(camPos, rd, normal);
    if (res.w != OUT){
        col2 = res.rgb;  
    }

    uv = fragCoord/iResolution.xy;

    // color stuff 
    //col2 = col2*col2*(3.0-2.0*col2);
    col2 *= 0.8 + 0.2*pow(16.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.4 );

    fragColor = vec4(col + col2,1.0);
}

