# info for model
modelPath = ''  # '.obj' needed
texturePath = ''
outputName = ''  # '.png'
treeDepth = 8

# save voxels data in a 8 bits RGBA texture
from PIL import Image
import numpy as np

class Poly:
    def __init__(self, vertex, uvs):
        self.vertex = vertex
        self.uvs = uvs


def split(polys: list, mid, axis=0):
    up = []
    down = []
    for poly in polys:
        vertex = np.array(poly.vertex)
        uv = poly.uvs.copy()
        idxUp = vertex[:, axis] > mid
        idxDown = vertex[:, axis] < mid
        idxOn = vertex[:, axis] == mid
        if np.all(idxUp | idxOn):  # all on one side
            up += [poly]

        elif np.all(idxDown | idxOn):  # all on the other side
            down += [poly]

        elif np.all(~ idxOn):
            idxUp2 = []
            idxDown2 = []
            idxOn2 = []
            vertex2 = []
            uv2 = []
            for i in range(len(poly.vertex)):
                idxUp2 += [idxUp[i]]
                idxDown2 += [idxDown[i]]
                idxOn2 += [idxOn[i]]
                vertex2 += [vertex[i]]
                uv2 += [uv[i]]
                if idxUp[i] != idxUp[(i + 1) % len(vertex)]:
                    if not (idxOn[i] or idxOn[(i + 1) % len(vertex)]):
                        A = vertex[i]
                        B = vertex[(i + 1) % len(vertex)]
                        x = (mid - A[axis]) / (A[axis] - B[axis])
                        v2 = A + (A - B) * x
                        vt2 = uv[i] + (uv[i] - uv[(i + 1) % len(vertex)]) * x
                        vertex2 += [v2]
                        uv2 += [vt2]
                        idxUp2 += [False]
                        idxDown2 += [False]
                        idxOn2 += [True]

            on = np.array(idxOn2, bool)
            vertex2 = np.array(vertex2)
            uv2 = np.array(uv2)
            up += [Poly(vertex2[idxUp2 | on], uv2[idxUp2 | on])]
            down += [Poly(vertex2[idxDown2 | on], uv2[idxDown2 | on])]

    return [down, up]


class Node:
    maxDepth = treeDepth
    Tree = []

    def __init__(self, depth=0):
        self.depth = depth
        self.child = None
        self.colors = []
        self.up = np.array([1, 1, 1], float)
        self.down = np.array([-1, -1, -1], float)

    def initChild(self):
        dxyz = self.up - self.down
        child = []
        for x in range(2):
            for y in range(2):
                for z in range(2):
                    c = Node(self.depth + 1)
                    c.down = self.down + np.array([x, y, z]) * dxyz / 2
                    c.up = self.down + dxyz / 2 + np.array([x, y, z]) * dxyz / 2
                    child += [c]
        return child

    def append(self, poly):
        if self.depth == Node.maxDepth:
            center = np.mean(poly.uvs, axis=0)
            center = np.array([center[0], 1.0 - center[1]])
            coord = np.array(center * texture.size, int)
            coord = np.minimum(coord + 1, texture.size) - 1
            data = texture.getpixel((coord[0], coord[1]))
            self.colors += [data[:3]]
            return

        if self.child is None:
            self.child = self.initChild()

        polys = [[poly]]
        # cut triangle in 8
        for axis in range(3):
            new = []
            mid = (self.up[axis] + self.down[axis]) / 2
            for i in range(2 ** axis):
                new += split(polys[i], mid, axis=axis)
            polys = new

        for i in range(8):
            for poly in polys[i]:
                self.child[i].append(poly)

    def treeDepth(self):
        if self.child is None:
            return self.depth
        return max(c.treeDepth() for c in self.child)

    def importObj(self, objfile):
        vertex = []
        vMin = np.zeros(3, float)
        vMax = np.zeros(3, float)
        uvs = []
        faceCount = 0
        with open(objfile, 'r') as f:  # get all vertex and texture info
            for line in f:
