                if line:
                    line = line.replace('  ', ' ')
                    data = line.split(' ')
                    if data[0] == 'v':
                        v = np.array([float(data[1]), float(data[2]), float(data[3])])
                        vMax = np.maximum(vMax, v)
                        vMin = np.minimum(vMin, v)
                        vertex += [v]
                    if data[0] == 'vt':
                        uvs += [np.array([float(data[1]), float(data[2])])]
                    if data[0] == 'f':
                        faceCount += 1
        # find vMin and vMax of the cube AABB
        vMid = 0.5 * (vMin + vMax)
        side = np.max(vMax - vMin)

        with open(objfile, 'r') as f:  # append all the faces
            i = 0
            for line in f:
