// To compute any part of the gamma function we only
// need to be able to compute it in the 0 to 1 range
// and the rest can be computed exactly from that
// by using these properties:
// (x + 1)! = x!(x + 1)
// (x - 1)! = x!/x

// Now using a least squares fit
// To approximate the 0 to 1 range

#define draw(d, c) color = mix(color, c, smoothstep(unit, 0.0, d))

// Approximate error (in the short range approximation): 0.00000317535198105
// The short range approximation is then analytically extended to the rest of the function
#define factorial01(x) (((((0.07288448978215456 * x - 0.31390051543712616) * x + 0.6538907084614038) * x - 0.810425715520978) * x + 0.9737655441276729) * x - 0.5761851668648887) * x + 0.9999830044034752
float factorial(in float x) {
    float h = floor(x), f = x - h, y = factorial01(f);
    if (x < 0.0) for (float n=0.0; n < -h; n++) y /= f - n;
    else for (float n=1.0; n < h + 1.0; n++) y *= f + n;
    return y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 8.0;
    float unit = 16.0 / iResolution.y;
    vec3 color = vec3(1.0);

    // Grid
    draw(abs(fract(uv.x + 0.5) - 0.5) + 0.25 * unit, vec3(0.0, 0.0, 1.0));
    draw(abs(fract(uv.y + 0.5) - 0.5) + 0.25 * unit, vec3(0.0, 0.0, 1.0));
    draw(abs(uv.x), vec3(1.0, 0.0, 0.0));
    draw(abs(uv.y), vec3(1.0, 0.0, 0.0));

    // Draw the function
    vec2 y = vec2(factorial(uv.x), 0.0);
    y.y = (factorial(uv.x + 0.001) - y.x) / 0.001;
    draw(abs(uv.y - y.x) / sqrt(1.0 + y.y * y.y) - 0.01, vec3(0.6, 0.0, 0.8));

    // Draw continuous Pascal's triangle
    //float n = uv.x + 8.0;
    //float k = uv.y + 4.0;
    //float binCoeff = round(factorial(n) / (factorial(k) * factorial(n - k)));
    //color = vec3(binCoeff * 0.01);

    fragColor = vec4(color, 1.0);
}

