             skyreflect = mix(skyreflect,texture(iChannel2,wr).rgb,.5);

             float wsun = dot( texNormalMap(wp.xz,1.), sunDir);
             wsun += dot( texNormalMap(wp.xz,0.25), sunDir )*2.;
