             wsun *= 0.143;
             wsun = clamp( wsun, 0.0, 1.0 )-0.6;
             float cst = caustics(wp.zx * 5. + wp.y,time*2.)*0.4 * clamp(wdistance,0.,1.)*clamp(wp.y/8.,0.,1.);

             vec3 c1 = mix(color(wp.xz,mix(3.-wdistfract*10.,wheight,.2)) + wsun,skyreflect,hilight) +cst;
