float voronoi(vec2 uv, float t, float seed, float size) {

    float minDist = 100.;

    float gridSize = size;

    vec2 cellUv = fract(uv * gridSize) - 0.5;
    vec2 cellCoord = floor(uv * gridSize);

    for (float x = -1.; x <= 1.; ++ x) {
        for (float y = -1.; y <= 1.; ++ y) {
            vec2 cellOffset = vec2(x,y);

            // Random 0-1 for each cell
            vec2 rand01Cell = rand01(cellOffset + cellCoord + seed);

            // Get position of point
            vec2 point = cellOffset + sin(rand01Cell * (t+10.)) * .5;

			// Get distance between pixel and point
            float dist = distFn(cellUv, point);
    		minDist = min(minDist, dist);
        }
    }

    return minDist;
}

vec3 fbm(in vec2 uv,sampler2D tex, float octaves)
{
uv = uv * 0.07;
float factor = 2.;
float facorinverse = 0.5;
vec3 result = texture(tex,uv).rgb;
    for(float i =0.; i < octaves;i++)
    {
    result += (mod(i,1.)*2.-1.)* (texture(tex,uv*factor).rgb)* facorinverse;
    factor *= 2.;
    facorinverse *= 0.5;
    }
    return result;
}


    //https://www.shadertoy.com/view/4sXXRj
vec3 waterripple(vec2 uv, sampler2D tex,float iTime)
    {
	vec2 p = uv * vec2(-1.0, -2.0);

    	float t = iTime/300.0;

		vec2 waterCoord = vec2(p.x, 1.0-uv.y);
		vec2 texCoordNormal0 = waterCoord * tile_factor;
		texCoordNormal0 += t ;

		vec2 texCoordNormal1 = waterCoord * tile_factor;
		texCoordNormal1.s -= t ;
		texCoordNormal1.t += t ;

		vec3 normal0 = texture(tex, texCoordNormal0).rgb * 2.0 - 1.0;
		vec3 normal1 = texture(tex, texCoordNormal1).rgb * 2.0 - 1.0;
		return normalize(normal0 + normal1);
    }

mat2 rotationMatrix(float angle)
{
    float s=sin(angle), c=cos(angle);
    return mat2( c, -s, s, c );
}

float caustics(vec2 uv,float time)
{
    float t = time * .35;

    float amplitude = .12;
    float turbulence = .5;
    vec2 uva = uv.xy + sin01(uv.x*turbulence + t) * amplitude;
    uva.xy -= cos01(uv.y*turbulence + t) * amplitude;
    vec2 uvb = uv.xy + sin01(uv.x*turbulence + t*1.3-2.) * amplitude * 0.7;
    uvb.xy -= cos01(uv.y*turbulence + t*1.-2.3) * amplitude * 0.7;
    uvb = uvb * rotationMatrix(1.04);
    vec2 uvc = uv.xy + sin01(uv.x*turbulence + t*1.4 + 3.) * amplitude * 0.3;
    uvc.xy -= cos01(uv.y*turbulence + t*1.4 + 3.) * amplitude * 0.3;
    uvc = uvc * rotationMatrix(2.08);

    float v;
    v += voronoi(uva, t * 2., 0.5, 2.5)/2.;
    v += voronoi(uvb, t * 3., 0., 3.) / 2.;
    v += voronoi(uvc, t * 4., 0., 4.) / 2.;

    return v;
}

