    s.xyz += 1.*vec3(-sin(iTime), 0, cos(iTime));
    //s.xz *= 5.;
    s.w = 0.3;
    //s.xz += vec2(sin(iTime), cos(iTime));
    dO = sphereDist(p, s);
    d = min(dO, d);



    return d; 
}

float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.;
    for(int i=0; i < MAX_STEPS; i++){
        vec3 p = ro + dO*rd;
        float dS = getDist(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;

}


vec3 getNormal(vec3 p){
    vec2 e = vec2(.01, 0);
    float d = getDist(p);
    vec3 n = d - vec3(
        getDist(p -e.xyy), 
        getDist(p -e.yxy), 
        getDist(p -e.yyx)
    );
    return normalize(n);
}


float getLight(vec3 p){

    //vec3 lightSource = vec3(10.*sin(iTime), 1., 3.*cos(iTime));
    vec3 lightSource = vec3(0., 3., 6.);
    //lightSource.xz += vec2(cos(iTime), sin(iTime));
    vec3 l = normalize(lightSource - p);
    vec3 n = getNormal(p);
    float dif = clamp(dot(l, n), 0., 1.);

    float d = RayMarch(p + n*SURF_DIST*3., l);
    if(d < length(p-lightSource)) dif *= 0.1;

    return dif;
}

