float hash12(vec2 p) {
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {    
    vec2 map = 1. / iResolution.xy;
    vec2 uv = fragCoord * map;

    vec4 surrounding = 
        texture(iChannel0, uv + vec2(-1, -1) * map) +
        texture(iChannel0, uv + vec2(-1, 0) * map) +
        texture(iChannel0, uv + vec2(-1, 1) * map) +
        texture(iChannel0, uv + vec2(0, -1) * map) +
        texture(iChannel0, uv + vec2(0, 1) * map) +
        texture(iChannel0, uv + vec2(1, -1) * map) +
        texture(iChannel0, uv + vec2(1, 0) * map) +
        texture(iChannel0, uv + vec2(1, 1) * map);

    int count = int(surrounding.w);

    vec4 self = texture(iChannel0, uv);
    float oldAlive = self.w;
    float newAlive = 0.;
    if (count == 3 || (oldAlive == 1. && count == 2)) newAlive = 1.; // core logic

    vec3 color;
    float rand = hash12(uv * 999. + iTime);

    if (iFrame == 0) {
        newAlive = step(0.5, rand);
        color = vec3(.5, .6, .9) * (uv + 0.3).xyx;
    } else if (iMouse.z > 0.001 && rand > 0.9 && distance(fragCoord, iMouse.xy) < (sin(iTime * 5.) / 4. + 1.) * 60.) {
        newAlive = 1.;
        color = vec3(.5, .7, .8) + vec2(sin(iTime), cos(iTime)).xyx / 3.;
    } else if (oldAlive == 0. && newAlive == 1.) {
        color = mix(surrounding.rgb / 8., self.rgb, .2) * 1.1;
    } else if (newAlive == 0.) {
        color = mix(surrounding.rgb / 8., self.rgb, .8) * 0.995;
    } else {
        color = self.rgb;
    }

    fragColor = vec4(color, newAlive);
}

