#define SHOW_TILING
#define TILES 3.0 // Use 1.0 for normal tiling across whole texture.
#define SCALE 40.0 // noise scale inside tile

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord / iResolution.xy;
	vec2 uv = (p-0.5) * vec2(1., iResolution.y/iResolution.x) + 0.5;


	#ifdef SHOW_TILING
	uv *= TILES;
	#endif

    vec4 f = noised(vec3(uv,iTime/SCALE), SCALE);
    f = f * 0.5 + 0.5;
    fragColor = mix( vec4(f.x), vec4(vec3(f.yzw),0.0) , vec4(round(fract(iTime*0.25f))) );


	#ifdef SHOW_TILING
	// Flash tile borders...
	vec2 pixel = TILES / iResolution.xy;
	if (mod(iTime-2.0, 4.0) < 2.0)
	{
		vec2 first 		= step(pixel, uv);
		uv  = step(fract(uv), pixel);	// Only add one line of pixels per tile.
		fragColor.xyz = mix(fragColor.xyz, vec3(1.0, 1.0, 0.0), (uv.x + uv.y) * first.x * first.y);
	}
	#endif    
}

