    }
    if (texelFetch(iChannel1, ivec2(SHIFT, 0), 0).x == 1.0 && paddlepos2.y + 0.1 < 0.47){
        paddlepos2.y += paddle_screen_per_frame/6.0;
    } else if (texelFetch(iChannel1, ivec2(CTRL, 0), 0).x == 1.0 && paddlepos2.y - 0.1 > -0.47){
        paddlepos2.y -= paddle_screen_per_frame/6.0;
    }
}

void pos(inout vec2 pos, in vec2 paddlepos, in vec2 paddlepos2, inout vec2 direction){ // check ball collision, move ball
    float ball_radius = 0.01;

    //hit barrier
    if (pos.y <= -(0.48) || pos.y >= 0.48 + ball_radius){
        direction = vec2(direction.x, -direction.y);
    }

    //hit paddle
    if (
        pos.x <= paddlepos.x + 0.01 + ball_radius &&
        pos.x >= paddlepos.x - (0.01 + ball_radius) &&
        pos.y <= paddlepos.y + 0.1 + ball_radius &&
        pos.y >= paddlepos.y-(0.1 + ball_radius)
    ){
        float hitpos = (pos.y-paddlepos.y)*9.0;
        direction = normalize(vec2( -( 1.0 - hitpos), hitpos));
        pos += sign(direction.x) * 0.01;
    }

    //hit paddle 2
    if (
        pos.x <= paddlepos2.x + 0.01 + ball_radius &&
        pos.x >= paddlepos2.x - (0.01 + ball_radius) &&
        pos.y <= paddlepos2.y + 0.1 + ball_radius &&
        pos.y >= paddlepos2.y-(0.1 + ball_radius)
    ){
        direction = vec2(-direction.x, direction.y);
        pos += sign(direction.x) * 0.01;
    }

    //leave map
    if (pos.x < -1.0) { //leave to left
        pos = vec2(0);
        direction = vec2(0);
        score.y ++;
    }
    if (pos.x > 1.0){ //leave to right
        pos = vec2(0);
        direction = vec2(0);
        score.x ++;
    }

    if (score.x < 5.0 && score.y < 5.0){
        pos += direction*ball_screen_per_frame/6.0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) //main
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col;

    //get frame data
    vec2 direction = texelFetch(iChannel0, ivec2(0, 0), 0).xy;
    vec2 currentpos = texelFetch(iChannel0, ivec2(1, 0), 0).xy;
    vec2 paddlepos = texelFetch(iChannel0, ivec2(2, 0), 0).xy;
    vec2 paddlepos2 = texelFetch(iChannel0, ivec2(3, 0), 0).xy;
    score = texelFetch(iChannel0, ivec2(4, 0), 0).xy;

    //fix missing data
    if (direction == vec2(0)){
        float ran_direction = rand( vec2( iTime)) - 0.5;
        float ran_side = sign( round( mod( rand( vec2( iTime)), 0.01)*100.0) - 0.5);
        direction = vec2((1.0 - ran_direction*sign(ran_direction))*ran_side, ran_direction);
    }
    if (paddlepos == vec2(0)){
        paddlepos = vec2(0.8, 0.0);
    }
    if (paddlepos2 == vec2(0)){
        paddlepos2 = vec2(-0.8, 0.0);
    }

    //set pos
    paddleposition(paddlepos, paddlepos2);
    pos(currentpos, paddlepos, paddlepos2, direction);

    //color in
    if (currentpos.x+0.01 >= uv.x && currentpos.x-0.01 <= uv.x && currentpos.y+0.01 >= uv.y && currentpos.y-0.01 <= uv.y){ col = vec3(1); } //ball

    if (uv.y>0.48 || uv.y<-0.48){ col = vec3(1); } //top + bottom border

    if (paddlepos.x+0.01 >= uv.x && paddlepos.x-0.01 <= uv.x && paddlepos.y+0.1 >= uv.y && paddlepos.y-0.1 <= uv.y){ col = vec3(1); } //paddle

    if (paddlepos2.x+0.01 >= uv.x && paddlepos2.x-0.01 <= uv.x && paddlepos2.y+0.1 >= uv.y && paddlepos2.y-0.1 <= uv.y){ col = vec3(1); } //paddle 2

    if (uv.x<0.005 && uv.x>-0.005 && mod(uv.y,0.04)>0.02){ col = vec3(1); } //middle border

    if ( draw_score(uv) ){ col = vec3(1); } // score

    if ( winlose(uv) ){ col = vec3(1); } //win / lose

    //check for restart
    if (texelFetch(iChannel1, ivec2(R, 0), 0).x == 1.0){
        score = vec2(0);
        paddlepos = vec2(0);
        paddlepos2 = vec2(0);
        currentpos = vec2(0);
        direction = vec2(0);
    }

    //store frame data
    ivec2 iCoord = ivec2(fragCoord);

    if (iCoord == ivec2(0,0)) {
        col = vec3(direction, 0.0); //ball direction
    }
    if (iCoord == ivec2(1,0)) {
        col = vec3(currentpos, 0.0); //ball position
    }
    if (iCoord == ivec2(2,0)) {
        col = vec3(paddlepos, 0.0); //paddle position
    }
    if (iCoord == ivec2(3,0)) {
        col = vec3(paddlepos2, 0.0); //paddle 2 position
    }
    if (iCoord == ivec2(4,0)) {
        col = vec3(score, 0.0); //scores
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

