vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/vec2(iResolution.y, iResolution.y);
    vec2 m = vec2(abs(sin(iTime /4.))* iResolution.x, abs(cos(iTime *.95)) * iResolution.y)/vec2(iResolution.y, iResolution.y);
    vec2 v = st - m;
    v = v / 0.01;
    float tau = 6.283185;
    float hue = atan(v.x, v.y)/tau;
    hue = hue + length(v)/80. - iTime/2.;
    vec3 rgb = hsl2rgb ( vec3(hue, 1., 
                              cos(2.0-length(v)*3.5)));
    fragColor = vec4(rgb.r,rgb.g,rgb.b,1.0);
}

const float pi  = 3.1416;
const float eps = 0.0001;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // make it look like the wiki page
    vec2 kl = fragCoord / iResolution.yy;
    // vertical flip
    kl.y = 1.0 - kl.y;
    // 8x8
    float MN = 8.0;
    kl *= MN;

    // lower case m,n vars
    vec2 mn = floor(kl);
    // JPEG flips every other?
    float sgn = mod(mn.x + mn.y, 2.0) * -2.0 + 1.0;

    // MD-DCT-IV, slightly vectorized
    vec2 dct = cos(mn*kl*pi);
    float col = dct.x * dct.y * sgn * 0.5 + 0.5;

    // red grid
    float w = 1.0 - eps - MN / iResolution.y;
    float g = step(w, fract(kl.x)) + step(w, fract(kl.y));
    // black out past MN (range)
    float r = step(kl.x, MN);

    // output to screen
    fragColor = vec4(mix(vec3(col), vec3(1.0, 0.0, 0.0), g) * r, 1.0);
}

