float circle( vec2 uv, vec2 center,float radius )
{
    return 1.0 - (smoothstep( radius -.02, radius + .02, length( uv - center ) ) );
}
float arc( vec2 uv, vec2 a, vec2 b )
{
    vec2 diff = b - a;
    float dist = length(diff);
    vec2 diff90 = vec2( diff.y, -diff.x );
    vec2 center = a + .866025 * diff90 + .5 * diff;
    float distToCenter = length( uv - center );

    float circleVal = max(.0, 1.0 - 40.0 * abs( distToCenter - dist ) );
    return circleVal * 
        clamp( 50.0 * dot( diff, uv - a), .0, 1.0 ) * 
        clamp( 50.0 * dot( -diff, uv - b), .0, 1.0 ) * 
        clamp( 50.0 * dot( uv - center, a - center), .0, 1.0 );

}
#define INV_MUL(a,b) (1.0-(1.0-(a))*(1.0-(b)))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float time = 2.* iTime + 1.5 * sin( iTime * .5 );
    vec2 uv = ( fragCoord - vec2( (iResolution.x - iResolution.y) * .5, 0 ) )/iResolution.y - vec2( .5 );

    // Time varying pixel color
    vec2 a = vec2( .3,.15) + vec2(sin(time*.9+32.)*0.1, sin(time*.2+124.)*.3);
    vec2 b = vec2( .1,-.2) + vec2(sin(time*.7+98.)*0.1, sin(time*.1+987.)*.35);
    vec2 c = vec2( -.4,.05) + vec2(sin(time*.8+24.)*0.15, sin(time*.3+156.)*.3);
    vec2 d = vec2( .0,.25) + vec2(sin(time*.75+95.)*0.07, sin(time*.25+696.)*.1);
    float col = circle( uv, a, .15 + sin(time*1.94+75.)*0.01);
    col = INV_MUL( col, circle( uv, b, .075 + sin(time*2.1+26.)*0.01 ) );
    col = INV_MUL( col, circle( uv, c, .1 + sin(time*1.3+81.)*0.02) );
    col = INV_MUL( col, circle( uv, d, .1 + sin(time*2.94+81.)*0.003) );
    col = INV_MUL( col, arc( uv, b,a ) );
    col = INV_MUL( col, arc( uv, b,c ) );
    col = INV_MUL( col, arc( uv, a,c ) );
    col = INV_MUL( col, arc( uv, d,c ) );
    col = INV_MUL( col, arc( uv, d,a ) );
    col = INV_MUL( col, arc( uv, d,b ) );
    float contour = clamp( 30.*(col-.55)*(col - .45),.0,1.0 );
    col = min(col, 1.0 );

    // Output to screen
    fragColor = vec4(contour - col * .2 ,contour - col * .07, contour,1.0);
}

