#define TONE(i,f) sin(c_tg*f*float(i))
#define TONE_UNTUNED( i, f,d) vec2(TONE(i,(1.-d)*f),TONE(i,(1.+d)*f))
#define LIMIT(x,l) clamp(x,-l,l)*1./l
#define TONE_MP(i,f,lp,hp) TONE(i,f)*clamp(min((f-lp)*.01,(hp-f)*.01),.0,1.)
#define RAND(i) (fract(8023.*sin(float(i)*0.0876))*2.-1.)
//#define RAND_INT(i,a) (((91331*(i)+15698)/((i)+11))%(a))
#define RAND_INT(i,a) ((int((RAND(i)+1.0)*1.2e3))%(a))


#define SPEED_UP_CYCLE 13

float att( int samp, int len, float attack )
{
    float dt = float( samp );
    float x = clamp( min( dt * attack,( float( len ) - dt ) )/ float( len ), .0, 1.0 );
    x *= x; x *= x; return x;
}

float kick( int samp, float c_tg )
{
    float sine = TONE( samp, 30. ) + TONE( samp, 50. ) + TONE( samp, 80. );
    sine *= att( samp, 15000, 1000.0 );
    return LIMIT( sine, .8 ) * .2;
}
vec2 bass ( int samp, float f, float c_tg )
{
    vec2 sine = TONE_UNTUNED( samp, f, .003 ) * .3 + TONE_UNTUNED( samp, f * 2., -.003 ) * .3;
    sine = vec2( LIMIT( sine.x, .8 ), LIMIT( sine.y, .8 ) );
    return sine;
}

float hat( int samp )
{

    float sine = RAND( samp % 8000 );
    sine -= .2 * RAND( ( samp - 4 ) % 8000 );
    sine -= .2 * RAND( ( samp - 3 ) % 8000 );
    sine -= .2 * RAND( ( samp - 2 ) % 8000 );
    sine -= .2 * RAND( ( samp - 1 ) % 8000 );
    sine -= .2 * RAND( ( samp + 1 ) % 8000 );
    sine -= .2 * RAND( ( samp + 2 ) % 8000 );
    sine -= .2 * RAND( ( samp + 3 ) % 8000 );
    sine -= .2 * RAND( ( samp + 4 ) % 8000 );

    sine *= att( samp, 18000, 30.0 );
    return sine * 0.02;
}

float snare( int samp )
{
    float sine = RAND( samp / 10 );
    float sine2 = RAND( samp / 3 );
    float sine3 = RAND( samp );
    sine *= att( samp, 17000, 1000.0 );
    sine2 *= att( samp, 15000, 3.0 );
    sine3 *= ( 
        att( samp, 5000, 10000.0 ) + 
        att( samp - 1500, 5000, 10000.0 ) * .4 + 
        att( samp - 3000, 5000, 10000.0 ) * .16 );

    return sine * .1 + sine2 * .3 + sine3 * .2;
}

