    17,16,14,13,11,10,9,13,9,5,0,0
    );

const int bassWalk[7] = int[7](
    1,3,5,7,8,7,3
    );

#define RHYTM_SAMPLE(samp) (samp+int(1000.*(TONE(samp,.3)+TONE(samp,.08))))

float chordLayer( int samp, float c_tg )
{
    int rhytmSamp = RHYTM_SAMPLE(samp);
    int phraseLength = beatSampCount * 12;


    int phraseIndexPrior = rhytmSamp / phraseLength;
    int speedUpMask = 1 - min( phraseIndexPrior % SPEED_UP_CYCLE, 1);
    int sustain = ( phraseLength * ( 1 + speedUpMask * 3) )/ 2;
    rhytmSamp += int( 2. * float( speedUpMask * rhytmSamp ) );



    int phraseIndex = rhytmSamp / phraseLength;
    int speedUpCount = ( phraseIndexPrior + SPEED_UP_CYCLE - 1 ) / SPEED_UP_CYCLE;
    phraseIndex -= speedUpCount;
    int phraseMod = phraseIndex & 7;
    int phraseSamp = (rhytmSamp % phraseLength );
    float f = progressionRatios[phraseMod] * 110.0 * (1.0 + .00004 - .00004 * TONE( samp, 8.02 ));


    float chord1f = f;
    float chord3f = relative3rds[phraseMod] * chord1f * 2.0;
    float chord5f = 1.5 * chord1f * 4.;
    float chord7f = relative7ths[phraseMod] * chord1f * 4.0;
    float chord9f =  2.25 * chord1f * 2.0;

    int sineSamp = samp % ( phraseLength * 5 );
    return

        TONE( sineSamp, chord1f) * .03 * att(phraseSamp, sustain, 100.0) + 
        TONE( sineSamp, chord3f) * .03 * att(phraseSamp - 8000, sustain, 100.0) + 
        TONE( sineSamp, chord5f) * .02 * att(phraseSamp - 3000, sustain, 100.0) + 
        TONE( sineSamp, chord7f) * .01 * att(phraseSamp, sustain, 100.0) + 
        TONE( sineSamp, chord9f) * .03 * att(phraseSamp - 3000, sustain, 100.0) + 
        TONE( sineSamp, chord9f) * .04 * att(phraseSamp - 13000, sustain, 100.0) +
        TONE( sineSamp, chord1f) * .03 * float(RAND_INT(phraseIndex+324,2)) * att(phraseSamp - beatSampCount*(2+RAND_INT(phraseIndex+933,6)), phraseLength/2, 100.0) + 
        TONE( sineSamp, chord3f) * .03 * float(RAND_INT(phraseIndex+957,2)) * att(phraseSamp - beatSampCount*(2+RAND_INT(phraseIndex+462,6)), phraseLength/2, 100.0) + 
        TONE( sineSamp, chord5f) * .02 * float(RAND_INT(phraseIndex+234,2)) * att(phraseSamp - beatSampCount*(2+RAND_INT(phraseIndex+116,6)), phraseLength/2, 100.0) + 
        TONE( sineSamp, chord7f) * .01 * float(RAND_INT(phraseIndex+634,2)) * att(phraseSamp - beatSampCount*(2+RAND_INT(phraseIndex+697,6)), phraseLength/2, 100.0) + 
        TONE( sineSamp, chord9f) * .03 * float(RAND_INT(phraseIndex+199,2)) * att(phraseSamp - beatSampCount*(2+RAND_INT(phraseIndex+555,6)), phraseLength/2, 100.0) + 
        TONE( sineSamp, chord9f) * .04 * float(RAND_INT(phraseIndex+846,2)) * att(phraseSamp - beatSampCount*(2+RAND_INT(phraseIndex+698,6)), phraseLength/2, 100.0);      
}

float beatLayer( int samp, float c_tg )
{
    int rythmSamp = RHYTM_SAMPLE(samp);
    int barLength = beatSampCount * 3;
    int barSamp = (rythmSamp % barLength );
    int barIndex = rythmSamp / barLength;
    int phraseLength = beatSampCount * 12;
    int phraseIndex = rythmSamp / phraseLength;
    int phraseMod = phraseIndex & 7;
    int phraseSamp = (rythmSamp % phraseLength );
    int kickOffset1 = (RAND_INT(phraseIndex+748,4)- 2);
    int kickOffset2 = kickOffset1 + (RAND_INT(phraseIndex+748,1 - kickOffset1) ) + 1;


     return 
        kick( phraseSamp, c_tg ) + 
        kick( phraseSamp - phraseLength / 2 + kickOffset1*beatSampCount , c_tg ) + 
        kick( phraseSamp - phraseLength / 2 + kickOffset2*beatSampCount , c_tg ) + 

        hat( phraseSamp ) +
        hat( barSamp - beatSampCount + 50 ) +
        hat( barSamp - beatSampCount - beatSampCount ) +
        hat( phraseSamp - beatSampCount / 2 - beatSampCount * ( RAND_INT( barIndex+493,13 ) ) ) +

        hat( phraseSamp - 3 * beatSampCount / 2) * .2+
        hat( barSamp - 3 * beatSampCount / 2 - beatSampCount + 50 ) * .2 +
        hat( barSamp - 3 * beatSampCount / 2 - beatSampCount - beatSampCount ) * .2 +
        snare( phraseSamp - phraseLength / 4  ) + 
        snare( phraseSamp - 3 * phraseLength / 4 );

}

vec2 mainSound( int samp, float time )
{
    float c_tg = 6.2831 / iSampleRate; // sine tangent   
    int rhytmSamp = RHYTM_SAMPLE(samp);
    int barLength = 3 * beatSampCount;
    int phraseLength = beatSampCount * 12;  
    int phraseIndexPrior = rhytmSamp / phraseLength;
    int speedUpMask = 1 - min( phraseIndexPrior % SPEED_UP_CYCLE, 1);
    rhytmSamp += int( 2. * float( speedUpMask * rhytmSamp ) );

    int beatSamp = (rhytmSamp % beatSampCount );
    int barSamp = (rhytmSamp % barLength );
    int barIndex = rhytmSamp / barLength;
    int beatIndex = rhytmSamp / beatSampCount;

    int phraseIndex = barIndex / 4;
    int speedUpCount = ( phraseIndexPrior + SPEED_UP_CYCLE - 1 ) / SPEED_UP_CYCLE;
    phraseIndex -= speedUpCount;

    int phraseSamp = (rhytmSamp % phraseLength );
    int phraseMod = phraseIndex & 7;
    int sineSamp = samp % ( phraseLength * 5 );


    float f = progressionRatios[phraseMod] * 110.0 * (1.0 + .000005 * TONE( samp, 8.02 ));
    float f2  = progressionRatios[phraseMod & 3] * 110.0 * (1.0 + .000005 * TONE( samp, 8.02 ));
    int cycleRange = int( iSampleRate / (f *4.) );
    float barBack = 4.0 *  (float (barSamp) / float(barLength) ) * (float (barLength-barSamp) / float(barLength) );

    int lickIndex = RAND_INT( (barIndex / 2), MELODY_LICK_COUNT);
    int melodyMod = (beatIndex % 6 ) + 6 * lickIndex;
    float melodyF1 = f2 * 4.0 * orderedRations[ melodyIndices[ 2 * melodyMod ] + phraseQualityOffset[phraseMod] ];
    float melodyF2 = f2 * 4.0 * orderedRations[ melodyIndices[ 2 * melodyMod + 1 ] + phraseQualityOffset[phraseMod]];
    float bassF = f2 * .25 * orderedRations[ bassWalk[ (barIndex*2) % 7 ] + phraseQualityOffset[phraseMod]];
    float bassF2 = f2 * .25 * orderedRations[ bassWalk[ (barIndex*2 + 1) % 7 ] + phraseQualityOffset[phraseMod]];

    float scratchMask = ( max( -TONE( rhytmSamp - int(100. * TONE( rhytmSamp, .4 ) ), .125 * iSampleRate / float(phraseLength) ), .96 ) - .96 ) * 25.0;
    scratchMask = max( scratchMask, clamp( .1 * time - 17.0,  .0, 1.0 ) );
    int scratchDistortion = int( 6000. * TONE( rhytmSamp, 4.0 ) * scratchMask );
    float maskFloat = 1.0 - float(speedUpMask);
    float melodyMask = float(speedUpCount & 1 );
    float tatTatMask = float((speedUpCount/2) & 1 );

    vec2 sound =
        vec2( beatLayer(samp + scratchDistortion, c_tg) ) * maskFloat +
        vec2( chordLayer(samp + scratchDistortion, c_tg) ) + 
        bass( sineSamp, bassF, c_tg ) * .3 * att(barSamp, barLength, 100.0) * maskFloat + 
        bass( sineSamp, bassF2, c_tg ) * .3 * att(barSamp - 2 * barLength, barLength, 100.0) * maskFloat + 
        TONE_UNTUNED( sineSamp, melodyF1,.01)* .6 * att(beatSamp, beatSampCount/2, 2.0) * maskFloat * melodyMask + 
        TONE_UNTUNED( sineSamp, melodyF2,-.02)* .2 * att(beatSamp - 2*beatSampCount/3, beatSampCount/3, 2.0) * maskFloat * melodyMask + 
        TONE_UNTUNED( sineSamp, 1.5 * f, .001 )*att(barSamp - beatSampCount + 50, beatSampCount, 10.0) * .2 * maskFloat * tatTatMask+ 
        TONE_UNTUNED( sineSamp, 1.5 * 1.2 * f,-.001 )*att(barSamp - beatSampCount - beatSampCount, beatSampCount, 10.0)* .2 * maskFloat * tatTatMask
        ;
    return sound * clamp( ( 180.0 - time ) * .2, .0, 1.0 );
}

